import { useState, useRef } from "react";
import Lottie from "lottie-react";
import "../styles/HaulWithRGM.css";
import characterAnimation from "../assets/animations/character.json";

function HaulWithRGM() {
  const [form, setForm] = useState({
    companyName: "",
    companyWebsite: "",
    name: "",
    phone: "",
    email: "",
    customerType: "",
    commodity: "",
    shipmentValue: "",
    shipmentFrequency: "",
    freightDetails: "",
    agreeSms: false,
    agreeEmail: false
  });

  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");
  const [hint, setHint] = useState("üëã Hi! I‚Äôll help you fill this rate quote form.");

  const lottieRef = useRef();

  const play = (from, to) => {
    if (lottieRef.current) lottieRef.current.playSegments([from, to], true);
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    const val = type === "checkbox" ? checked : value;
    setForm((prev) => ({ ...prev, [name]: val }));

    // Dynamic hints based on field
    switch (name) {
      case "companyName":
      case "companyWebsite":
      case "name":
        setHint(val.length < 2 ? "‚ö†Ô∏è Looks too short" : "‚úÖ Looks good");
        play(0, 30);
        break;
      case "email":
        setHint(/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(val) ? "üìß Email looks valid" : "‚ùå Invalid email");
        play(31, 60);
        break;
      case "phone":
        setHint(val.replace(/\D/g, "").length < 10 ? "üì± Must be 10 digits" : "üìû Phone looks valid");
        play(61, 90);
        break;
      case "agreeSms":
      case "agreeEmail":
        if (checked) {
          setHint("üéâ Great! You can submit now");
          play(91, 120);
        }
        break;
      default:
        setHint("‚úçÔ∏è Keep going!");
        play(0, 30);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!form.email) {
      setMessage("‚ùå Email is required");
      play(121, 150);
      return;
    }
    if (!form.agreeSms && !form.agreeEmail) {
      setMessage("‚ùå Please agree to at least one communication method");
      play(121, 150);
      return;
    }

    try {
      setLoading(true);
      setMessage("");
      setHint("üöÄ Submitting your rate quote...");
      play(151, 180);

      const res = await fetch("http://localhost:5000/rate-quote", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form)
      });

      const data = await res.json();
      if (!res.ok) throw new Error(data.message || "Submission failed");

      setMessage("üéâ Rate quote submitted successfully!");
      setHint("ü•≥ Your request has been sent!");
      play(181, 210);

      // Reset form
      setForm({
        companyName: "",
        companyWebsite: "",
        name: "",
        phone: "",
        email: "",
        customerType: "",
        commodity: "",
        shipmentValue: "",
        shipmentFrequency: "",
        freightDetails: "",
        agreeSms: false,
        agreeEmail: false
      });
    } catch (err) {
      setMessage(`‚ùå ${err.message}`);
      setHint("‚ö†Ô∏è Could not submit. Try again.");
      play(121, 150);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="haul-container">
      <h1>Get Your Rate Quote</h1>
      <p>Truckload Capacity Available ‚Äì Get your freight on the road fast, safe, and efficiently with Melton Truck Lines.</p>

      <div className="character-wrapper">
        <Lottie animationData={characterAnimation} loop lottieRef={lottieRef} className="character-animation" />
        <div className="speech-bubble">{hint}</div>
      </div>

      <form className="haul-form" onSubmit={handleSubmit}>
        <div className="section-card full">
          <input name="companyName" placeholder="Company Name *" required value={form.companyName} onChange={handleChange} />
          <input name="companyWebsite" placeholder="Company Website *" value={form.companyWebsite} onChange={handleChange} />
          <input name="name" placeholder="Name *" required value={form.name} onChange={handleChange} />
          <input name="phone" placeholder="Phone Number (xxx-xxx-xxxx) *" required value={form.phone} onChange={handleChange} />
          <input name="email" placeholder="Email *" type="email" required value={form.email} onChange={handleChange} />
        </div>

        <div className="section-card full">
          <input name="customerType" placeholder="Customer Type *" value={form.customerType} onChange={handleChange} />
          <input name="commodity" placeholder="Commodity *" value={form.commodity} onChange={handleChange} />
          <input name="shipmentValue" placeholder="Dollar Value of Shipment *" value={form.shipmentValue} onChange={handleChange} />
          <select name="shipmentFrequency" value={form.shipmentFrequency} onChange={handleChange}>
            <option value="">Shipment Frequency</option>
            <option value="1-5 loads/week">1-5 loads/week</option>
            <option value="6+ loads/week">6+ loads/week</option>
            <option value="One time or occasional shipment">One time or occasional shipment</option>
          </select>
          <textarea name="freightDetails" placeholder="Tell us more about your freight needs" value={form.freightDetails} onChange={handleChange}></textarea>
        </div>

        <div className="section-card full">
          <label className="checkbox-container">
            <input type="checkbox" name="agreeSms" checked={form.agreeSms} onChange={handleChange} />
            By checking this box, you agree to receive SMS communications from Melton at the phone number provided.
          </label>

          <label className="checkbox-container">
            <input type="checkbox" name="agreeEmail" checked={form.agreeEmail} onChange={handleChange} />
            By checking this box, you agree to receive email communications from us, including updates, promotions, and relevant industry insights.
          </label>
        </div>

        {message && <div className="form-message">{message}</div>}

        <button className="submit-btn full" type="submit" disabled={loading}>
          {loading ? "Submitting..." : "Submit"}
        </button>

        <div className="form-note">
          By submitting this form, you agree that Melton Truck Lines may contact you via email or phone. You may opt out at any time. Message and data rates may apply.
        </div>
      </form>
    </div>
  );
}

export default HaulWithRGM;
