import React, { useEffect, useState, useRef } from "react";
import "../styles/HaulWithRGM.css";
import Lottie from "lottie-react";

/* BOT ANIMATION */
import characterAnimation from "../assets/animations/character.json";

/* HERO SLIDES */
import hero1 from "../assets/images/hero/hero12.jpg";
import hero2 from "../assets/images/hero/hero14.jpg";
import hero3 from "../assets/images/hero/hero15.jpg";

const heroImages = [hero1, hero2, hero3];

const HaulWithRGM = () => {
  const [slide, setSlide] = useState(0);
  const [botOpen, setBotOpen] = useState(true);
  const [hint, setHint] = useState("ðŸ‘‹ Hi! Iâ€™ll help you fill this form.");

  const lottieRef = useRef(null);

  const [form, setForm] = useState({
    company: "",
    website: "",
    name: "",
    phone: "",
    email: "",
    customerType: "",
    commodity: "",
    dollarValue: "",
    frequency: "",
    details: "",
    smsConsent: false,
    emailConsent: false,
  });

  /* HERO SLIDER */
  useEffect(() => {
    const timer = setInterval(() => {
      setSlide((prev) => (prev + 1) % heroImages.length);
    }, 5000);
    return () => clearInterval(timer);
  }, []);

  /* SAFE LOTTIE PLAY */
  const play = (from, to) => {
    if (lottieRef.current) {
      lottieRef.current.playSegments([from, to], true);
    }
  };

  /* FORM HANDLER */
  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    const val = type === "checkbox" ? checked : value;

    setForm((prev) => ({ ...prev, [name]: val }));

    switch (name) {
      case "company":
        setHint("ðŸ¢ Company name looks good");
        play(0, 30);
        break;
      case "website":
        setHint("ðŸŒ Website added");
        play(0, 30);
        break;
      case "name":
        setHint("ðŸ˜Š Nice to meet you!");
        play(0, 30);
        break;
      case "phone":
        setHint(
          val.length >= 10
            ? "ðŸ“ž Phone number looks valid"
            : "ðŸ“ž Please enter a valid phone number"
        );
        play(31, 60);
        break;
      case "email":
        setHint(
          /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(val)
            ? "ðŸ“§ Email verified"
            : "ðŸ“§ Enter a valid email address"
        );
        play(61, 90);
        break;
      case "frequency":
        setHint("ðŸšš Shipment frequency noted");
        play(91, 120);
        break;
      case "details":
        setHint("ðŸ“ Extra details help us serve you better");
        play(121, 150);
        break;
      default:
        setHint("ðŸ‘ Youâ€™re doing great!");
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setHint("ðŸŽ‰ Submitted! Our team will contact you shortly.");
    play(151, 180);
    alert("âœ… Your rate quote has been submitted!");
  };

  return (
    <div className="haulwithrgm-page">
      {/* HERO */}
      <section className="haulwithrgm-hero">
        <img src={heroImages[slide]} alt="Flatbed Freight" />
        <div className="haulwithrgm-hero-overlay">
          <div className="haulwithrgm-hero-content">
            <h1>Get Your Rate Quote</h1>
            <p>
              Truckload Capacity Available â€“ Get your freight on the road fast,
              safe, and efficiently with Melton Truck Lines.
            </p>
          </div>
        </div>
      </section>

      {/* FORM */}
      <section className="haulwithrgm-form-section">
        <div className="haulwithrgm-card">
          <h2>Request a Free Rate Quote</h2>
          <p className="subtitle">
            Fill out the form and our logistics experts will respond quickly.
          </p>

          {/* BOT TOGGLE */}
          <button
            className="bot-toggle"
            onClick={() => setBotOpen(!botOpen)}
          >
            {botOpen ? "âœ– Hide Assistant" : "ðŸ’¬ Need Help?"}
          </button>

          {/* BOT */}
          {botOpen && (
            <div className="character-wrapper">
              <Lottie
                animationData={characterAnimation}
                lottieRef={lottieRef}
                loop
                className="character-animation"
              />
              <div className="speech-bubble">{hint}</div>
            </div>
          )}

          <form className="haulwithrgm-form" onSubmit={handleSubmit}>
            <input name="company" placeholder="Company Name *" required onChange={handleChange} />
            <input name="website" placeholder="Company Website *" required onChange={handleChange} />
            <input name="name" placeholder="Name *" required onChange={handleChange} />
            <input name="phone" placeholder="Phone Number *" required onChange={handleChange} />
            <input name="email" type="email" placeholder="Email *" required onChange={handleChange} />
            <input name="customerType" placeholder="Customer Type *" required onChange={handleChange} />
            <input name="commodity" placeholder="Commodity *" required onChange={handleChange} />
            <input name="dollarValue" placeholder="Dollar Value of Shipment *" required onChange={handleChange} />

            <select name="frequency" required onChange={handleChange}>
              <option value="">Shipment Frequency *</option>
              <option>1-5 loads/week</option>
              <option>6+ loads/week</option>
              <option>One time or occasional shipment</option>
            </select>

            <textarea
              name="details"
              placeholder="Tell us more about your freight needs"
              onChange={handleChange}
            />

            <label className="checkbox-container">
              <input type="checkbox" name="smsConsent" onChange={handleChange} />
              I agree to receive SMS communications.
            </label>

            <label className="checkbox-container">
              <input type="checkbox" name="emailConsent" onChange={handleChange} />
              I agree to receive email communications.
            </label>

            <button type="submit" className="haulwithrgm-submit">
              Submit
            </button>
          </form>
        </div>
      </section>
    </div>
  );
};

export default HaulWithRGM;
