import { useState, useEffect, useRef } from "react";
import Lottie from "lottie-react";
import "../styles/HaulWithRGM.css";
import characterAnimation from "../assets/animations/character.json";

function HaulWithRGM() {
  const [form, setForm] = useState({
    companyName: "",
    website: "",
    name: "",
    phone: "",
    email: "",
    customerType: "",
    commodity: "",
    value: "",
    frequency: "",
    details: "",
    smsConsent: false,
    emailConsent: false
  });

  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");

  /* ü§ñ BOT STATES */
  const [hint, setHint] = useState("");
  const [typedText, setTypedText] = useState("");
  const lottieRef = useRef(null);

  /* üéØ BOT SPEAK FUNCTION */
  const speak = (text, from = 0, to = 30) => {
    setHint(text);
    setTypedText("");
    if (lottieRef.current) {
      lottieRef.current.playSegments([from, to], true);
    }
  };

  /* ‚å®Ô∏è TYPING EFFECT */
  useEffect(() => {
    let i = 0;
    if (!hint) return;

    const interval = setInterval(() => {
      setTypedText((prev) => prev + hint.charAt(i));
      i++;
      if (i >= hint.length) clearInterval(interval);
    }, 25);

    return () => clearInterval(interval);
  }, [hint]);

  useEffect(() => {
    speak("Hi! I‚Äôm RGM Assistant üëã Let‚Äôs get your rate quote started üöö");
  }, []);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    const val = type === "checkbox" ? checked : value;

    setForm((prev) => ({ ...prev, [name]: val }));

    if (name === "companyName") speak("Great! Company name noted üëç");
    else if (name === "email") speak("Make sure your email is correct üìß");
    else if (name === "phone") speak("Perfect! We‚Äôll contact you here üìû");
    else if (name === "details") speak("Thanks for the details! Almost done ‚ú®");
    else if (name === "smsConsent" || name === "emailConsent")
      speak("Consent received ‚úî You can submit now üöÄ", 120, 160);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!form.smsConsent && !form.emailConsent) {
      speak("Please give at least one consent to continue ‚ö†Ô∏è", 90, 120);
      return;
    }

    try {
      setLoading(true);
      speak("Submitting your request... üöÄ", 160, 200);

      const res = await fetch("http://localhost:5000/rate-quote", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form)
      });

      const data = await res.json();
      if (!res.ok) throw new Error(data.message);

      setMessage("‚úÖ Rate quote sent successfully!");
      speak("Success! Our RGM team will contact you soon üéâ", 200, 240);

      setForm({
        companyName: "",
        website: "",
        name: "",
        phone: "",
        email: "",
        customerType: "",
        commodity: "",
        value: "",
        frequency: "",
        details: "",
        smsConsent: false,
        emailConsent: false
      });
    } catch (err) {
      speak("Oops! Something went wrong. Please try again ‚ùå", 90, 120);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="haul-container">
      <h1>Get Your Rate Quote</h1>
      <p>Truckload capacity available ‚Äî fast, safe & reliable with RGM</p>

      {/* ü§ñ BOT */}
      <div className="character-wrapper">
        <Lottie
          animationData={characterAnimation}
          lottieRef={lottieRef}
          loop
          className="character-animation"
        />
        <div className="speech-bubble">{typedText}</div>
      </div>

      {/* üìã FORM */}
      <form className="haul-form" onSubmit={handleSubmit}>
        <input name="companyName" placeholder="Company Name *" required value={form.companyName} onChange={handleChange} />
        <input name="website" placeholder="Company Website" value={form.website} onChange={handleChange} />
        <input name="name" placeholder="Your Name *" required value={form.name} onChange={handleChange} />
        <input name="phone" placeholder="Phone Number *" required value={form.phone} onChange={handleChange} />
        <input name="email" type="email" placeholder="Email *" required value={form.email} onChange={handleChange} />

        <select name="frequency" value={form.frequency} onChange={handleChange}>
          <option value="">Shipment Frequency</option>
          <option>1-5 loads/week</option>
          <option>6+ loads/week</option>
          <option>One-time shipment</option>
        </select>

        <textarea
          name="details"
          placeholder="Tell us more about your freight needs"
          value={form.details}
          onChange={handleChange}
        />

        {/* ‚úÖ CONSENT */}
        <div className="consent-section">
          <label className="consent-item">
            <input type="checkbox" name="smsConsent" checked={form.smsConsent} onChange={handleChange} />
            <span>I agree to receive SMS communications from RGM.</span>
          </label>

          <label className="consent-item">
            <input type="checkbox" name="emailConsent" checked={form.emailConsent} onChange={handleChange} />
            <span>I agree to receive email communications from RGM.</span>
          </label>
        </div>

        {message && <div className="form-message">{message}</div>}

        <button type="submit" disabled={loading}>
          {loading ? "Submitting..." : "Submit Rate Quote"}
        </button>
      </form>
    </div>
  );
}

export default HaulWithRGM;
