import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "../styles/Haul.css";

import hero1 from "../assets/images/hero/hero1.jpg";
import hero2 from "../assets/images/hero/hero2.jpg";
import hero3 from "../assets/images/hero/hero3.jpg";

const images = [hero1, hero2, hero3];

const Haul = () => {
  const [index, setIndex] = useState(0);

  /* HERO SLIDESHOW */
  useEffect(() => {
    const timer = setInterval(() => {
      setIndex((i) => (i + 1) % images.length);
    }, 6000);
    return () => clearInterval(timer);
  }, []);

  /* SCROLL REVEAL */
  useEffect(() => {
    const els = document.querySelectorAll(".haul-reveal");
    const observer = new IntersectionObserver(
      (entries) =>
        entries.forEach(
          (e) => e.isIntersecting && e.target.classList.add("visible")
        ),
      { threshold: 0.2 }
    );
    els.forEach((el) => observer.observe(el));
    return () => els.forEach((el) => observer.unobserve(el));
  }, []);

  return (
    <div className="haul-page">
      {/* ================= HERO ================= */}
      <section className="haul-hero">
        <img src={images[index]} alt="Flatbed Freight" />

        <div className="haul-hero-overlay">
          <div className="haul-hero-content">
            <h1>Experts in Flatbed Freight</h1>
            <p>
              Industry-leading flatbed & specialized hauling across the USA,
              Canada, and Mexico.
            </p>

            <div className="haul-hero-buttons">
              <Link to="/HaulWithRGM" className="haul-btn primary">
                Free Rate Quote
              </Link>
              <Link to="/contact" className="haul-btn secondary">
                Contact Us
              </Link>
            </div>
          </div>
        </div>
      </section>

      {/* ================= INTRO ================= */}
      <section className="haul-intro haul-reveal">
        <h2>What is Flatbed Freight?</h2>
        <p>
          Flatbed shipping is ideal for oversized, heavy, or non-enclosed cargo.
          Our flatbed trailers provide unmatched flexibility for construction
          materials, machinery, steel, tires, and industrial loads.
        </p>
      </section>

      {/* ================= SERVICES ================= */}
      <section className="haul-services haul-reveal">
        <div className="haul-service-card">Canada Domestic Freight Services</div>
        <div className="haul-service-card">Cross-Border Freight Services</div>
        <div className="haul-service-card">
          Over-Dimensional Flatbed Freight
        </div>
      </section>

      {/* ================= BENEFITS ================= */}
      <section className="haul-benefits haul-reveal">
        <h2>Why Ship Flatbed Freight with RGM</h2>

        <ul className="haul-benefit-list">
          <li className="benefit-item">
            Protect your high-value cargo and enjoy true peace of mind.
          </li>

          <li className="benefit-item">
            Scale up to <strong>48,000 lbs across the USA</strong> and up to{" "}
            <strong>55,000 lbs within Canada</strong>, ensuring your cargo spends
            the least possible time in transit.
          </li>

          <li className="benefit-item">
            Late-model equipment, regularly maintained with controlled preventive
            maintenance, ensures greater reliability and less time on the road.
          </li>

          <li className="benefit-item">
            Adaptable, personalized shipping solutions throughout North America.
          </li>
        </ul>
      </section>

      {/* ================= EQUIPMENT ================= */}
      <section className="haul-benefits haul-reveal">
        <h2>RGMâ€™s Flatbed Shipping Equipment</h2>

        <ul className="haul-benefit-list">
          <li className="benefit-item">
            Every driver goes through a thorough hiring process, including an
            RCMP background check, verified experience through a letter of
            experience from their previous insurance company, a drug test, and
            seven pre-employment training certifications through Carrier Edge.
          </li>

          <li className="benefit-item">
            Each truck comes equipped with standardized tarps, securement, and
            dunnage, ensuring every RGM driver is fully prepared to safely handle
            any customer load.
          </li>
        </ul>
      </section>

      {/* ================= CTA ================= */}
      <section className="haul-cta">
        <h2>Move Your Freight with Confidence</h2>
        <p>
          From standard flatbed loads to complex specialized freight, RGM
          Logistics delivers performance you can trust.
        </p>

        <div className="haul-cta-buttons">
          <Link to="/HaulWithRGM" className="haul-btn primary">
            Request a Free Quote
          </Link>
          <Link to="/contact" className="haul-btn secondary">
            Speak with Our Team
          </Link>
        </div>
      </section>
    </div>
  );
};

export default Haul;
