import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "../styles/Haul.css";

// HERO IMAGES
import hero1 from "../assets/images/hero/hero1.jpg";
import hero2 from "../assets/images/hero/hero2.jpg";
import hero3 from "../assets/images/hero/hero3.jpg";
import hero4 from "../assets/images/hero/hero4.jpg";
import hero5 from "../assets/images/hero/hero5.jpg";

const heroImages = [hero1, hero2, hero3, hero4, hero5];

const Haul = () => {
  const [currentImage, setCurrentImage] = useState(0);
  const navigate = useNavigate();

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentImage((prev) => (prev + 1) % heroImages.length);
    }, 5000);

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="haul-page">
      {/* ================= HERO ================= */}
      <section
        className="haul-hero"
        style={{ backgroundImage: `url(${heroImages[currentImage]})` }}
      >
        <div className="haul-overlay">
          <div className="haul-hero-content">
            <h1 className="haul-heading">
              Flatbed & Specialized Hauling
            </h1>
            <p className="haul-subheading">
              Trusted nationwide freight solutions for oversized, heavy, and
              time-critical shipments.
            </p>
          </div>

          <div className="haul-hero-buttons">
            <button
              className="haul-btn primary"
              onClick={() => navigate("/haul-with-rgm")}
            >
              Free Rate Quote
            </button>

            <button
              className="haul-btn secondary"
              onClick={() => navigate("/contact")}
            >
              Contact Us
            </button>
          </div>
        </div>
      </section>

      {/* ================= MAIN CONTENT ================= */}
      <section className="haul-section">
        <h2>Experts in Flatbed Freight</h2>
        <p>
          RGM provides reliable flatbed and specialized hauling services across
          the United States. With modern equipment, experienced drivers, and a
          safety-first approach, we ensure your freight arrives on time and
          damage-free.
        </p>

        <div className="haul-features">
          <div className="feature-card">
            <h3>Nationwide Coverage</h3>
            <p>
              We operate across all major routes with reliable scheduling and
              flexible logistics solutions.
            </p>
          </div>

          <div className="feature-card">
            <h3>Modern Equipment</h3>
            <p>
              Late-model trucks and trailers designed to safely haul oversized
              and heavy freight.
            </p>
          </div>

          <div className="feature-card">
            <h3>Safety First</h3>
            <p>
              Highly trained drivers and strict safety protocols for maximum
              cargo protection.
            </p>
          </div>
        </div>
      </section>

      {/* ================= CTA ================= */}
      <section className="haul-cta">
        <h2>Ready to Move Your Freight?</h2>
        <p>
          Get a fast, competitive quote today and experience professional
          flatbed hauling with RGM.
        </p>
        <button
          className="haul-cta-btn"
          onClick={() => navigate("/haul-with-rgm")}
        >
          Get Free Quote
        </button>
      </section>
    </div>
  );
};

export default Haul;
