import { useState, useRef, useEffect } from "react";
import Lottie from "lottie-react";
import "../styles/HaulWithRGM.css";
import characterAnimation from "../assets/animations/character.json";

function HaulWithRGM() {
  const [form, setForm] = useState({
    companyName: "",
    companyWebsite: "",
    name: "",
    phone: "",
    email: "",
    customerType: "",
    commodity: "",
    shipmentValue: "",
    shipmentFrequency: "",
    freightDetails: "",
    agreeSms: false,
    agreeEmail: false,
  });

  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");
  const [hint, setHint] = useState("üëã Hi! Let's get your RGM rate quote.");
  const [typingHint, setTypingHint] = useState("");
  const lottieRef = useRef();

  // Function to play Lottie animation segments
  const play = (from, to) => {
    if (lottieRef.current) {
      lottieRef.current.playSegments([from, to], true);
    }
  };

  // Typing animation for the bot
  const showTypingHint = (text) => {
    setTypingHint("");
    let i = 0;
    const interval = setInterval(() => {
      setTypingHint((prev) => prev + text.charAt(i));
      i++;
      if (i >= text.length) clearInterval(interval);
    }, 25);
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    let val = type === "checkbox" ? checked : value;

    // Only digits for phone
    if (name === "phone") val = val.replace(/\D/g, "");

    setForm((prev) => ({ ...prev, [name]: val }));

    // Interactive bot hints with emojis
    let hintText = "‚úçÔ∏è Keep going...";
    if (name === "companyName") hintText = val.length < 2 ? "üè¢ Company name seems short" : "‚úÖ Company name saved!";
    else if (name === "email") hintText = /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(val) ? "üìß Email looks good" : "‚ùå Invalid email format";
    else if (name === "phone") hintText = val.length < 10 ? "üì± Enter a 10-digit phone number" : "üìû Phone number looks valid";
    else if (type === "checkbox" && checked) hintText = "üëç Consent noted";
    showTypingHint(hintText);

    // Lottie animations
    if (name === "companyName" || name === "name") play(0, 30);
    else if (name === "email") play(61, 90);
    else if (name === "phone") play(91, 120);
    else if (type === "checkbox" && checked) play(151, 180);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!form.agreeSms && !form.agreeEmail) {
      showTypingHint("‚ùå Please agree to at least one communication method");
      play(121, 150);
      return;
    }

    try {
      setLoading(true);
      setMessage("");
      showTypingHint("üöö Submitting your rate quote...");
      play(151, 180);

      const res = await fetch("http://localhost:5000/rate-quote", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form),
      });

      let data;
      try {
        data = await res.json();
      } catch {
        data = { message: res.statusText || "Submission failed" };
      }

      if (!res.ok) throw new Error(data.message || "Submission failed");

      setMessage("üéâ Rate quote submitted successfully!");
      showTypingHint("ü•≥ Our RGM team will contact you soon!");
      play(181, 210);

      setForm({
        companyName: "",
        companyWebsite: "",
        name: "",
        phone: "",
        email: "",
        customerType: "",
        commodity: "",
        shipmentValue: "",
        shipmentFrequency: "",
        freightDetails: "",
        agreeSms: false,
        agreeEmail: false,
      });
    } catch (err) {
      setMessage(`‚ùå ${err.message}`);
      showTypingHint("‚ö†Ô∏è Something went wrong. Please try again.");
      play(121, 150);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="haul-container">
      <h1>Get Your Rate Quote</h1>
      <p>Truckload Capacity Available ‚Äì Get your freight on the road fast, safe, and efficiently with RGM Logistics.</p>

      <div className="character-wrapper">
        <Lottie animationData={characterAnimation} loop lottieRef={lottieRef} className="character-animation" />
        <div className="speech-bubble">{typingHint}</div>
      </div>

      <form className="haul-form" onSubmit={handleSubmit}>
        <div className="section-card full">
          <h3>Company Information</h3>
          <input name="companyName" placeholder="Company Name *" required value={form.companyName} onChange={handleChange} />
          <input name="companyWebsite" placeholder="Company Website *" required value={form.companyWebsite} onChange={handleChange} />
          <input name="name" placeholder="Name *" required value={form.name} onChange={handleChange} />
          <input name="phone" placeholder="Phone Number (xxx-xxx-xxxx) *" required value={form.phone} onChange={handleChange} />
          <input name="email" type="email" placeholder="Email *" required value={form.email} onChange={handleChange} />
        </div>

        <div className="section-card full">
          <h3>Shipment Details</h3>
          <select name="customerType" required value={form.customerType} onChange={handleChange}>
            <option value="">Customer Type *</option>
            <option value="Shipper">Shipper</option>
            <option value="Broker">Broker</option>
            <option value="Manufacturer">Manufacturer</option>
          </select>
          <input name="commodity" placeholder="Commodity *" required value={form.commodity} onChange={handleChange} />
          <input name="shipmentValue" placeholder="Dollar Value of Shipment *" required value={form.shipmentValue} onChange={handleChange} />
          <select name="shipmentFrequency" required value={form.shipmentFrequency} onChange={handleChange}>
            <option value="">Shipment Frequency *</option>
            <option value="1-5">1‚Äì5 loads/week</option>
            <option value="6+">6+ loads/week</option>
            <option value="one-time">One-time / occasional</option>
          </select>
          <textarea name="freightDetails" placeholder="Tell us more about your freight needs" value={form.freightDetails} onChange={handleChange} rows="4" />
        </div>

        <div className="section-card full consent-section">
          <label className="checkbox-container">
            <input type="checkbox" name="agreeSms" checked={form.agreeSms} onChange={handleChange} />
            By checking this box, you agree to receive SMS communications from RGM at the phone number provided. I agree
          </label>
          <label className="checkbox-container">
            <input type="checkbox" name="agreeEmail" checked={form.agreeEmail} onChange={handleChange} />
            By checking this box, you agree to receive email communications from RGM, including updates, promotions, and relevant industry insights. I agree
          </label>
        </div>

        <p className="form-note">
          By checking ‚ÄúI agree‚Äù and submitting this rate quote form, you agree that RGM Logistics is permitted to contact you via the email and/or phone number provided above. You may opt out of communications at any time. Message and data rates may apply. For more information, please view our Privacy Policy.
        </p>

        {message && <div className="form-message">{message}</div>}

        <button className="submit-btn full" type="submit" disabled={loading}>
          {loading ? "Submitting..." : "Submit"}
        </button>
      </form>
    </div>
  );
}

export default HaulWithRGM;
