import React, { useEffect } from "react";
import { Link } from "react-router-dom";
import "../styles/Haul.css";

import hero1 from "../assets/images/hero/hero1.jpg";
import hero2 from "../assets/images/hero/hero2.jpg";
import hero3 from "../assets/images/hero/hero3.jpg";

const images = [hero1, hero2, hero3];

const Haul = () => {
  useEffect(() => {
    let i = 0;
    const hero = document.querySelector(".haul-hero");

    const interval = setInterval(() => {
      hero.style.backgroundImage = `url(${images[i]})`;
      i = (i + 1) % images.length;
    }, 6000);

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="haul-page">
      {/* HERO */}
      <section className="haul-hero">
        <div className="haul-overlay">
          <div className="haul-hero-content">
            <h1 className="haul-heading">
              Flatbed & Specialized Hauling
            </h1>
            <p className="haul-subheading">
              Trusted nationwide freight solutions for oversized, heavy,
              and time-critical shipments.
            </p>
          </div>

          <div className="haul-hero-buttons">
            <Link to="/HaulWithRGM">
              <button className="btn-primary">Free Rate Quote</button>
            </Link>
            <Link to="/Contact">
              <button className="btn-secondary">Contact Us</button>
            </Link>
          </div>
        </div>
      </section>

      {/* CONTENT */}
      <section className="haul-section">
        <h2>Experts in Flatbed Freight</h2>
        <p>
          RGM Logistics delivers long-haul flatbed trucking solutions across
          all 48 contiguous states, Canada, and Mexico. Our late-model fleet
          and experienced drivers ensure cargo security, reliability,
          and on-time performance.
        </p>
      </section>

      {/* SERVICES */}
      <section className="haul-section light">
        <h2>Our Flatbed Services</h2>
        <div className="haul-grid">
          <div>USA Domestic Freight</div>
          <div>Canada Freight Services</div>
          <div>Mexico Freight Services</div>
          <div>Over-Dimensional Loads</div>
        </div>
      </section>

      {/* BENEFITS */}
      <section className="haul-section">
        <h2>Why Ship With RGM</h2>
        <ul className="haul-list">
          <li>100% company-owned trucks & drivers</li>
          <li>Late-model, well-maintained equipment</li>
          <li>Flexible routing & trailer pools</li>
          <li>Safety-first culture & reliable delivery</li>
        </ul>
      </section>

      {/* CTA */}
      <section className="haul-cta">
        <h2>Move Your Freight With Confidence</h2>
        <Link to="/HaulWithRGM">
          <button className="btn-primary large">
            Request a Free Quote
          </button>
        </Link>
      </section>
    </div>
  );
};

export default Haul;
