import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "../styles/Haul.css";

/* HERO IMAGES */
import hero1 from "../assets/images/hero/hero1.jpg";
import hero2 from "../assets/images/hero/hero2.jpg";
import hero3 from "../assets/images/hero/hero3.jpg";
import hero4 from "../assets/images/hero/hero4.jpg";
import hero5 from "../assets/images/hero/hero5.jpg";
import hero6 from "../assets/images/hero/hero6.jpg";
import hero7 from "../assets/images/hero/hero7.jpg";
import hero8 from "../assets/images/hero/hero8.jpg";
import hero9 from "../assets/images/hero/hero9.jpg";
import hero10 from "../assets/images/hero/hero10.jpg";

const heroImages = [
  hero1, hero2, hero3, hero4, hero5,
  hero6, hero7, hero8, hero9, hero10
];

const Haul = () => {
  const [current, setCurrent] = useState(0);
  const navigate = useNavigate();

  /* HERO SLIDER */
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrent((prev) => (prev + 1) % heroImages.length);
    }, 5000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="haul-page">

      {/* ================= HERO ================= */}
      <section
        className="haul-hero"
        style={{ backgroundImage: `url(${heroImages[current]})` }}
      >
        <div className="haul-overlay">
          <div className="haul-hero-content">
            <h1 className="haul-heading">
              Experts in Flatbed Freight
            </h1>

            <p className="haul-subheading">
              Industry-leading flatbed trucking solutions across North America
            </p>

            <div className="haul-hero-buttons">
              <button
                className="haul-btn primary"
                onClick={() => navigate("/haul-with-rgm")}
              >
                Free Rate Quote
              </button>

              <button
                className="haul-btn secondary"
                onClick={() => navigate("/contact")}
              >
                Contact Us
              </button>
            </div>
          </div>
        </div>
      </section>

      {/* ================= INTRO ================= */}
      <section className="haul-section">
        <h2>Reliable Flatbed & Specialized Hauling</h2>
        <p>
          RGM Family is a trusted flatbed carrier delivering oversized,
          heavy, and time-critical freight with precision and care.
          Our modern fleet of late-model trucks and 53-foot air-ride
          flatbed and step-deck trailers minimizes cargo damage and delays.
        </p>

        <p>
          Unlike carriers with limited routing, RGM provides long-haul
          flatbed solutions across all 48 contiguous U.S. states,
          Canada, and Mexico — making us your one-stop partner
          for safe and on-time delivery.
        </p>
      </section>

      {/* ================= SERVICES ================= */}
      <section className="haul-section light">
        <h2>Our Flatbed Freight Services</h2>

        <div className="haul-grid">
          <div className="haul-card">USA Domestic Freight Services</div>
          <div className="haul-card">Canada Freight Services</div>
          <div className="haul-card">Mexico Freight Services</div>
          <div className="haul-card">Over-Dimensional Flatbed Freight</div>
        </div>
      </section>

      {/* ================= WHAT IS FLATBED ================= */}
      <section className="haul-section">
        <h2>What is Flatbed Freight?</h2>
        <p>
          Flatbed shipping is ideal for freight that is oversized,
          unusually shaped, or does not require an enclosed trailer.
          Flatbed and step-deck trailers offer easy access for loading,
          unloading, and flexible cargo dimensions.
        </p>

        <p>
          Common flatbed freight includes construction materials,
          machinery, steel, lumber, industrial equipment, and more.
        </p>
      </section>

      {/* ================= BENEFITS ================= */}
      <section className="haul-section light">
        <h2>Benefits of Shipping with RGM</h2>

        <ul className="haul-benefits">
          <li>Customized flatbed solutions across North America</li>
          <li>100% company-owned trucks and professional drivers</li>
          <li>Trailer pools to reduce downtime and increase scalability</li>
          <li>Cross-border expertise for Canada & Mexico freight</li>
          <li>Covered cargo protection facilities</li>
          <li>Late-model, well-maintained equipment for fewer delays</li>
          <li>Strong safety performance and CSA compliance</li>
        </ul>
      </section>

      {/* ================= EQUIPMENT ================= */}
      <section className="haul-section">
        <h2>Our Flatbed Equipment</h2>

        <p>
          Every RGM truck is equipped with standardized tarps,
          securement tools, and dunnage to safely handle any load.
          Our drivers undergo extensive training in flatbed
          securement and tarping procedures.
        </p>

        <div className="haul-grid">
          <div className="haul-card">
            <h4>53′ Flatbed Trailers</h4>
            <p>48,000 lb payload • Air-ride suspension</p>
          </div>

          <div className="haul-card">
            <h4>53′ Step Deck Trailers</h4>
            <p>46,000 lb payload • Multi-height loading</p>
          </div>
        </div>
      </section>

      {/* ================= CTA ================= */}
      <section className="haul-cta">
        <h2>Ready to Ship With Confidence?</h2>
        <p>
          Get a fast, competitive flatbed rate today.
        </p>

        <button
          className="haul-btn primary large"
          onClick={() => navigate("/haul-with-rgm")}
        >
          Get a Free Rate Quote
        </button>
      </section>

    </div>
  );
};

export default Haul;
