import React, { useEffect, useState } from "react";
import "../styles/HaulWithRGM.css";

/* HERO SLIDES */
import hero1 from "../assets/images/hero/hero12.jpg";
import hero2 from "../assets/images/hero/hero14.jpg";
import hero3 from "../assets/images/hero/hero15.jpg";

const heroImages = [hero1, hero2, hero3];

const HaulWithRGM = () => {
  const [slide, setSlide] = useState(0);
  const [botOpen, setBotOpen] = useState(false);

  const [form, setForm] = useState({
    company: "",
    name: "",
    phone: "",
    email: "",
    commodity: "",
    frequency: "",
    details: "",
  });

  /* HERO SLIDESHOW */
  useEffect(() => {
    const timer = setInterval(
      () => setSlide((s) => (s + 1) % heroImages.length),
      5000
    );
    return () => clearInterval(timer);
  }, []);

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = (e) => {
    e.preventDefault();
    alert("âœ… Quote submitted successfully!");
  };

  return (
    <div className="haulwithrgm-page">
      {/* ================= HERO ================= */}
      <section className="haulwithrgm-hero">
        <img src={heroImages[slide]} alt="Flatbed Freight" />
        <div className="haulwithrgm-hero-overlay">
          <div className="haulwithrgm-hero-content">
            <h1>Request a Flatbed Rate Quote</h1>
            <p>
              Reliable, scalable flatbed freight solutions across North America.
            </p>
          </div>
        </div>
      </section>

      {/* ================= FORM SECTION ================= */}
      <section className="haulwithrgm-form-section">
        <div className="haulwithrgm-card">
          <h2>Tell Us About Your Freight</h2>
          <p className="subtitle">
            Complete the form and our logistics experts will respond fast.
          </p>

          <form className="haulwithrgm-form" onSubmit={handleSubmit}>
            <input
              name="company"
              placeholder="Company Name *"
              required
              onChange={handleChange}
            />
            <input
              name="name"
              placeholder="Contact Name *"
              required
              onChange={handleChange}
            />
            <input
              name="phone"
              placeholder="Phone Number *"
              required
              onChange={handleChange}
            />
            <input
              name="email"
              type="email"
              placeholder="Email Address *"
              required
              onChange={handleChange}
            />

            <input
              name="commodity"
              placeholder="Commodity *"
              required
              onChange={handleChange}
            />

            <select name="frequency" required onChange={handleChange}>
              <option value="">Shipment Frequency *</option>
              <option>One-time</option>
              <option>Weekly</option>
              <option>Daily / Dedicated</option>
            </select>

            <textarea
              name="details"
              placeholder="Additional freight details"
              className="full"
              onChange={handleChange}
            />

            <button className="haulwithrgm-submit full" type="submit">
              Get My Free Quote
            </button>
          </form>
        </div>
      </section>

      {/* ================= CHAT BOT ================= */}
      <div className={`rgm-bot ${botOpen ? "open" : ""}`}>
        <div className="rgm-bot-header" onClick={() => setBotOpen(!botOpen)}>
          ðŸ¤– RGM Assistant
        </div>

        {botOpen && (
          <div className="rgm-bot-body">
            <p>Hi ðŸ‘‹ How can I help you today?</p>
            <button>Request a Quote</button>
            <button>Talk to Support</button>
          </div>
        )}
      </div>
    </div>
  );
};

export default HaulWithRGM;
