import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "../styles/Haul.css";

/* HERO & MAIN IMAGES */
import hero12 from "../assets/images/hero/hero12.jpg";
import hero3 from "../assets/images/hero/hero3.jpg";
import hero11 from "../assets/images/hero/hero14.jpg";
import hero13 from "../assets/images/hero/hero15.jpg";

const heroImages = [hero12, hero3, hero11, hero13];
const midImages = [hero3, hero11, hero13, hero12];
const introImages = [hero12, hero3, hero11, hero13]; // Slideshow for intro
const visualStripImages = [hero3, hero11, hero13, hero12]; // Slideshow for visual strip

const Haul = () => {
  const [heroIndex, setHeroIndex] = useState(0);
  const [introIndex, setIntroIndex] = useState(0);
  const [midIndex, setMidIndex] = useState(0);
  const [visualIndex, setVisualIndex] = useState(0);

  /* HERO SLIDESHOW */
  useEffect(() => {
    const timer = setInterval(() => {
      setHeroIndex((i) => (i + 1) % heroImages.length);
    }, 6000);
    return () => clearInterval(timer);
  }, []);

  /* INTRO SLIDESHOW */
  useEffect(() => {
    const timer = setInterval(() => {
      setIntroIndex((i) => (i + 1) % introImages.length);
    }, 6000);
    return () => clearInterval(timer);
  }, []);

  /* MID SLIDESHOW */
  useEffect(() => {
    const timer = setInterval(() => {
      setMidIndex((i) => (i + 1) % midImages.length);
    }, 5000);
    return () => clearInterval(timer);
  }, []);

  /* VISUAL STRIP SLIDESHOW */
  useEffect(() => {
    const timer = setInterval(() => {
      setVisualIndex((i) => (i + 1) % visualStripImages.length);
    }, 5000);
    return () => clearInterval(timer);
  }, []);

  /* SCROLL REVEAL */
  useEffect(() => {
    const elements = document.querySelectorAll(".haul-reveal");
    const observer = new IntersectionObserver(
      (entries) =>
        entries.forEach((entry) => {
          if (entry.isIntersecting) entry.target.classList.add("visible");
        }),
      { threshold: 0.2 }
    );
    elements.forEach((el) => observer.observe(el));
    return () => elements.forEach((el) => observer.unobserve(el));
  }, []);

  return (
    <div className="haul-page">
      {/* ================= HERO ================= */}
      <section className="haul-hero">
        <img src={heroImages[heroIndex]} alt="Flatbed Freight" className="haul-hero-image-full" />
        <div className="haul-hero-overlay">
          <div className="haul-hero-content">
            <h1 className="company-color">Experts in Flatbed Freight</h1>
            <p>
              Industry-leading flatbed & specialized hauling across the USA,
              Canada, and Mexico.
            </p>
            <div className="haul-hero-buttons">
              <Link to="/HaulWithRGM" className="haul-btn primary">Free Rate Quote</Link>
              <Link to="/contact" className="haul-btn secondary">Contact Us</Link>
            </div>
          </div>
        </div>
      </section>

      {/* ================= INTRO (SIDE IMAGE + SLIDESHOW) ================= */}
      <section className="haul-intro haul-reveal">
        <div className="haul-intro-content">
          <div className="haul-intro-text">
            <h2 className="company-color">What is Flatbed Freight?</h2>
            <p>
              Flatbed shipping is ideal for oversized, heavy, or non-enclosed cargo.
              Our flatbed trailers provide unmatched flexibility for construction
              materials, machinery, steel, tires, and industrial loads.
            </p>
          </div>
          <div className="haul-intro-image">
            <img src={introImages[introIndex]} alt="Flatbed Operations" />
          </div>
        </div>
      </section>

      {/* ================= MID SLIDESHOW ================= */}
      <section className="haul-mid-slideshow haul-reveal">
        <div className="haul-mid-slideshow-wrapper">
          <img src={midImages[midIndex]} alt="RGM Flatbed Operations" className="haul-mid-image" />
        </div>
      </section>

      {/* ================= SERVICES ================= */}
      <section className="haul-services haul-reveal">
        <div className="haul-service-card">Canada Domestic Freight Services</div>
        <div className="haul-service-card">Cross-Border Freight Services</div>
        <div className="haul-service-card">Over-Dimensional Flatbed Freight</div>
      </section>

      {/* ================= BENEFITS ================= */}
      <section className="haul-benefits haul-reveal">
        <h2 className="company-color">Why Ship Flatbed Freight with RGM</h2>
        <ul className="haul-benefit-list">
          <li className="benefit-item">Protect your high-value cargo and enjoy true peace of mind.</li>
          <li className="benefit-item">
            Scale up to <strong>48,000 lbs across the USA</strong> and <strong>55,000 lbs within Canada</strong> with minimal transit time.
          </li>
          <li className="benefit-item">Late-model equipment with preventive maintenance for maximum reliability.</li>
          <li className="benefit-item">Adaptable, personalized shipping solutions throughout North America.</li>
        </ul>
      </section>

      {/* ================= EQUIPMENT ================= */}
      <section className="haul-benefits haul-reveal">
        <h2 className="company-color">RGMâ€™s Flatbed Shipping Equipment</h2>
        <ul className="haul-benefit-list">
          <li className="benefit-item">Every driver undergoes RCMP background checks, verified experience, drug testing, and seven Carrier Edge certifications.</li>
          <li className="benefit-item">Every truck is fully equipped with standardized tarps, securement, and dunnage to handle any customer load safely.</li>
        </ul>
      </section>

      {/* ================= VISUAL STRIP SLIDESHOW ================= */}
      <section className="haul-mid-slideshow haul-reveal">
        <div className="haul-mid-slideshow-wrapper">
          <img src={visualStripImages[visualIndex]} alt="RGM Fleet" className="haul-mid-image" />
        </div>
      </section>

      {/* ================= CTA ================= */}
      <section className="haul-cta">
        <h2>Move Your Freight with Confidence</h2>
        <p>From standard flatbed loads to complex specialized freight, RGM Logistics delivers performance you can trust.</p>
        <div className="haul-cta-buttons">
          <Link to="/HaulWithRGM" className="haul-btn primary">Request a Free Quote</Link>
          <Link to="/contact" className="haul-btn secondary">Speak with Our Team</Link>
        </div>
      </section>
    </div>
  );
};

export default Haul;
