import { useState, useRef } from "react";
import Lottie from "lottie-react";
import "../styles/HaulWithRGM.css";
import characterAnimation from "../assets/animations/character.json";

function RateQuote() {
  const [form, setForm] = useState({
    companyName: "",
    companyWebsite: "",
    name: "",
    phone: "",
    email: "",
    customerType: "",
    commodity: "",
    shipmentValue: "",
    shipmentFrequency: "",
    freightDetails: "",
    agreeSms: false,
    agreeEmail: false
  });

  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");
  const [hint, setHint] = useState("üëã Hi! Let‚Äôs get your RGM rate quote.");

  const lottieRef = useRef();

  const play = (from, to) => {
    lottieRef.current?.playSegments([from, to], true);
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    let val = type === "checkbox" ? checked : value;

    if (name === "phone") val = val.replace(/\D/g, "");
    setForm((prev) => ({ ...prev, [name]: val }));

    if (type === "checkbox" && checked) {
      setHint("üëç Consent noted");
      play(151, 180);
      return;
    }

    if (name === "email") {
      const ok = /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(val);
      setHint(ok ? "üìß Email looks good" : "‚ùå Invalid email format");
      play(ok ? 61 : 121, ok ? 90 : 150);
      return;
    }

    setHint("‚úçÔ∏è Tell us more about your freight needs");
    play(0, 30);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!form.agreeEmail && !form.agreeSms) {
      setMessage("‚ùå Please agree to at least one communication method");
      play(121, 150);
      return;
    }

    try {
      setLoading(true);
      setHint("üöö Submitting your rate quote...");
      play(151, 180);

      const res = await fetch("http://localhost:5000/apply", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form)
      });

      if (!res.ok) throw new Error("Submission failed");

      setMessage("üéâ Rate quote request submitted successfully!");
      setHint("ü•≥ Our RGM team will contact you soon!");
      play(181, 210);

      setForm({
        companyName: "",
        companyWebsite: "",
        name: "",
        phone: "",
        email: "",
        customerType: "",
        commodity: "",
        shipmentValue: "",
        shipmentFrequency: "",
        freightDetails: "",
        agreeSms: false,
        agreeEmail: false
      });

    } catch {
      setMessage("‚ùå Something went wrong. Please try again.");
      play(121, 150);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="apply-container">
      <h1>Get Your Rate Quote</h1>
      <p>Truckload capacity available ‚Äì fast, safe, and efficient with RGM Logistics.</p>

      <div className="character-wrapper">
        <Lottie
          animationData={characterAnimation}
          loop
          lottieRef={lottieRef}
          className="character-animation"
        />
        <div className="speech-bubble">{hint}</div>
      </div>

      <form className="apply-form" onSubmit={handleSubmit}>
        {/* FORM SECTIONS OMITTED FOR BREVITY ‚Äì KEEP YOUR EXISTING INPUTS */}

        <div className="section-card full">
          <label className="checkbox-row">
            <input
              type="checkbox"
              name="agreeSms"
              checked={form.agreeSms}
              onChange={handleChange}
            />
            <span>I agree to receive SMS communications from RGM Logistics</span>
          </label>

          <label className="checkbox-row">
            <input
              type="checkbox"
              name="agreeEmail"
              checked={form.agreeEmail}
              onChange={handleChange}
            />
            <span>I agree to receive email communications from RGM Logistics</span>
          </label>
        </div>

        {message && <div className="form-message">{message}</div>}

        <button className="submit-btn" disabled={loading}>
          {loading ? "Submitting..." : "Submit Rate Quote"}
        </button>
      </form>
    </div>
  );
}

export default RateQuote;
