import { useState, useRef, useEffect } from "react";
import Lottie from "lottie-react";
import "../styles/HaulWithRGM.css";
import characterAnimation from "../assets/animations/character.json";

function HaulWithRGM() {
  const [form, setForm] = useState({
    companyName: "",
    companyWebsite: "",
    name: "",
    phone: "",
    email: "",
    customerType: "",
    commodity: "",
    shipmentValue: "",
    shipmentFrequency: "",
    freightDetails: "",
    agreeSms: false,
    agreeEmail: false
  });

  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");
  const [displayHint, setDisplayHint] = useState("");
  const [hint, setHint] = useState("üëã Hi! Let‚Äôs get your RGM rate quote.");

  const lottieRef = useRef();

  /* üéØ Typing animation for bot text */
  useEffect(() => {
    let i = 0;
    setDisplayHint("");
    const interval = setInterval(() => {
      setDisplayHint((prev) => prev + hint.charAt(i));
      i++;
      if (i >= hint.length) clearInterval(interval);
    }, 30);
    return () => clearInterval(interval);
  }, [hint]);

  const play = (from, to) => {
    if (lottieRef.current) {
      lottieRef.current.playSegments([from, to], true);
    }
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    let val = type === "checkbox" ? checked : value;

    if (name === "phone") val = val.replace(/\D/g, "");

    setForm((prev) => ({ ...prev, [name]: val }));

    if (name === "companyName") {
      setHint(val.length < 2 ? "üè¢ Company name looks short" : "‚úÖ Company name saved");
      play(0, 30);
    } 
    else if (name === "email") {
      const valid = /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(val);
      setHint(valid ? "üìß Email looks good" : "‚ùå Invalid email format");
      play(valid ? 61 : 121, valid ? 90 : 150);
    } 
    else if (name === "phone") {
      setHint(val.length < 10 ? "üì± Enter 10 digits" : "üìû Phone looks valid");
      play(val.length < 10 ? 121 : 91, val.length < 10 ? 150 : 120);
    } 
    else if (type === "checkbox" && checked) {
      setHint("üëç Consent noted");
      play(151, 180);
    } 
    else {
      setHint("‚úçÔ∏è Tell me a bit more");
      play(0, 30);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!form.agreeSms && !form.agreeEmail) {
      setMessage("‚ùå Please agree to at least one option");
      setHint("‚ö†Ô∏è Consent required");
      play(121, 150);
      return;
    }

    try {
      setLoading(true);
      setMessage("");
      setHint("üöö Sending your request...");
      play(151, 180);

      const res = await fetch("http://localhost:5000/rate-quote", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form),
      });

      const data = await res.json();
      if (!res.ok) throw new Error(data.message);

      setMessage("üéâ Rate quote submitted!");
      setHint("ü•≥ Our RGM team will contact you soon");
      play(181, 210);

      setForm({
        companyName: "",
        companyWebsite: "",
        name: "",
        phone: "",
        email: "",
        customerType: "",
        commodity: "",
        shipmentValue: "",
        shipmentFrequency: "",
        freightDetails: "",
        agreeSms: false,
        agreeEmail: false
      });
    } catch (err) {
      setMessage("‚ùå Submission failed");
      setHint("‚ö†Ô∏è Try again");
      play(121, 150);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="apply-container">
      <h1>Get Your Rate Quote</h1>
      <p>Fast, safe truckload solutions with RGM Logistics.</p>

      <div className="character-wrapper">
        <Lottie
          animationData={characterAnimation}
          loop
          lottieRef={lottieRef}
          className="character-animation"
        />
        <div className="speech-bubble">{displayHint}</div>
      </div>

      <form className="apply-form" onSubmit={handleSubmit}>
        <div className="section-card full">
          <input name="companyName" placeholder="Company Name *" required value={form.companyName} onChange={handleChange} />
          <input name="companyWebsite" placeholder="Company Website *" required value={form.companyWebsite} onChange={handleChange} />
          <input name="name" placeholder="Name *" required value={form.name} onChange={handleChange} />
          <input name="phone" placeholder="Phone Number *" required value={form.phone} onChange={handleChange} />
          <input name="email" placeholder="Email *" required value={form.email} onChange={handleChange} />
        </div>

        <div className="section-card full">
          <select name="customerType" required value={form.customerType} onChange={handleChange}>
            <option value="">Customer Type *</option>
            <option>Shipper</option>
            <option>Broker</option>
            <option>Manufacturer</option>
          </select>
          <input name="commodity" placeholder="Commodity *" required value={form.commodity} onChange={handleChange} />
          <input name="shipmentValue" placeholder="Shipment Value *" required value={form.shipmentValue} onChange={handleChange} />
          <select name="shipmentFrequency" required value={form.shipmentFrequency} onChange={handleChange}>
            <option value="">Shipment Frequency *</option>
            <option>1‚Äì5 loads/week</option>
            <option>6+ loads/week</option>
            <option>One-time shipment</option>
          </select>
          <textarea name="freightDetails" placeholder="Tell us more about your freight" value={form.freightDetails} onChange={handleChange} />
        </div>

        <div className="section-card full">
          <label className="checkbox-row">
            <input type="checkbox" name="agreeSms" checked={form.agreeSms} onChange={handleChange} />
            <span>I agree to receive SMS from RGM</span>
          </label>

          <label className="checkbox-row">
            <input type="checkbox" name="agreeEmail" checked={form.agreeEmail} onChange={handleChange} />
            <span>I agree to receive emails from RGM</span>
          </label>
        </div>

        {message && <div className="form-message">{message}</div>}

        <button className="submit-btn full" disabled={loading}>
          {loading ? "Submitting..." : "Submit Rate Quote"}
        </button>
      </form>
    </div>
  );
}

export default HaulWithRGM;
