import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "../styles/Haul.css";

/* ================= HERO IMAGES ================= */
import hero12 from "../assets/images/hero/hero12.jpg";
import hero3 from "../assets/images/hero/hero3.jpg";
import hero11 from "../assets/images/hero/hero14.jpg";
import hero13 from "../assets/images/hero/hero15.jpg";

/* ================= GALLERY IMAGES (INSIDE SRC) ================= */
import g30 from "../assets/images/gallery/gallery62.jpg";
import g31 from "../assets/images/gallery/gallery63.jpg";
import g32 from "../assets/images/gallery/gallery51.jpg";
import g33 from "../assets/images/gallery/gallery52.jpg";
import g34 from "../assets/images/gallery/gallery53.jpg";
import g35 from "../assets/images/gallery/gallery54.jpg";
import g36 from "../assets/images/gallery/gallery55.jpg";
import g37 from "../assets/images/gallery/gallery56.jpg";
import g38 from "../assets/images/gallery/gallery57.jpg";

/* ================= IMAGE ARRAYS ================= */
const heroImages = [hero12, hero3, hero11, hero13];
const midImages = [g30, g31, g32, g33, g34, g35, g36, g37, g38];

const Haul = () => {
  const [heroIndex, setHeroIndex] = useState(0);
  const [midIndex, setMidIndex] = useState(0);

  /* ================= HERO SLIDESHOW ================= */
  useEffect(() => {
    const timer = setInterval(() => {
      setHeroIndex((prev) => (prev + 1) % heroImages.length);
    }, 6000);
    return () => clearInterval(timer);
  }, []);

  /* ================= MID SLIDESHOW ================= */
  useEffect(() => {
    const timer = setInterval(() => {
      setMidIndex((prev) => (prev + 1) % midImages.length);
    }, 5000);
    return () => clearInterval(timer);
  }, []);

  /* ================= SCROLL REVEAL ================= */
  useEffect(() => {
    const elements = document.querySelectorAll(".haul-reveal");

    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            entry.target.classList.add("visible");
          }
        });
      },
      { threshold: 0.2 }
    );

    elements.forEach((el) => observer.observe(el));

    return () => elements.forEach((el) => observer.unobserve(el));
  }, []);

  return (
    <div className="haul-page">
      {/* ================= HERO ================= */}
      <section className="haul-hero">
        <img
          src={heroImages[heroIndex]}
          alt="Flatbed Freight"
          className="haul-hero-img"
        />

        <div className="haul-hero-overlay">
          <div className="haul-hero-content">
            <h1>Experts in Flatbed Freight</h1>
            <p>
              Industry-leading flatbed & specialized hauling across the USA,
              Canada, and Mexico.
            </p>

            <div className="haul-hero-buttons">
              <Link to="/HaulWithRGM" className="haul-btn primary">
                Free Rate Quote
              </Link>
              <Link to="/contact" className="haul-btn secondary">
                Contact Us
              </Link>
            </div>
          </div>
        </div>
      </section>

      {/* ================= INTRO ================= */}
      <section className="haul-intro haul-reveal">
        <h2>What is Flatbed Freight?</h2>
        <p>
          Flatbed shipping is ideal for oversized, heavy, or non-enclosed cargo.
          Our trailers offer unmatched flexibility for construction materials,
          machinery, steel, tires, and industrial freight.
        </p>
      </section>

      {/* ================= MID SLIDESHOW ================= */}
      <section className="haul-mid-slideshow haul-reveal">
        <img
          src={midImages[midIndex]}
          alt="RGM Flatbed Operations"
          className="haul-mid-img"
        />
      </section>

      {/* ================= SERVICES ================= */}
      <section className="haul-services haul-reveal">
        <div className="haul-service-card">
          Canada Domestic Freight Services
        </div>
        <div className="haul-service-card">
          Cross-Border Freight Services
        </div>
        <div className="haul-service-card">
          Over-Dimensional Flatbed Freight
        </div>
      </section>

      {/* ================= BENEFITS ================= */}
      <section className="haul-benefits haul-reveal">
        <h2>Why Ship Flatbed Freight with RGM</h2>

        <ul className="haul-benefit-list">
          <li>High-value cargo protection with complete peace of mind</li>
          <li>
            Up to <strong>48,000 lbs in the USA</strong> and{" "}
            <strong>55,000 lbs in Canada</strong>
          </li>
          <li>Late-model equipment with proactive maintenance</li>
          <li>Customized flatbed solutions across North America</li>
        </ul>
      </section>

      {/* ================= EQUIPMENT ================= */}
      <section className="haul-benefits haul-reveal">
        <h2>RGMâ€™s Flatbed Shipping Equipment</h2>

        <ul className="haul-benefit-list">
          <li>
            RCMP-verified drivers with experience, drug testing, and Carrier
            Edge certifications
          </li>
          <li>
            Standardized tarps, securement, and dunnage on every truck
          </li>
        </ul>
      </section>

      {/* ================= VISUAL STRIP ================= */}
      <section className="haul-mid-slideshow haul-reveal">
        <img
          src={midImages[(midIndex + 1) % midImages.length]}
          alt="Fleet"
          className="haul-mid-img"
        />
      </section>

      {/* ================= CTA ================= */}
      <section className="haul-cta">
        <h2>Move Your Freight with Confidence</h2>
        <p>
          From standard flatbed loads to complex specialized freight, RGM
          Logistics delivers performance you can trust.
        </p>

        <div className="haul-cta-buttons">
          <Link to="/HaulWithRGM" className="haul-btn primary">
            Request a Free Quote
          </Link>
          <Link to="/contact" className="haul-btn secondary">
            Speak with Our Team
          </Link>
        </div>
      </section>
    </div>
  );
};

export default Haul;
