import React, { useEffect, useState } from "react";
import "../styles/HaulWithRGM.css";

/* HERO SLIDES */
import hero1 from "../assets/images/hero/hero12.jpg";
import hero2 from "../assets/images/hero/hero14.jpg";
import hero3 from "../assets/images/hero/hero15.jpg";

const heroImages = [hero1, hero2, hero3];

const HaulWithRGM = () => {
  const [slide, setSlide] = useState(0);
  const [form, setForm] = useState({
    company: "",
    website: "",
    name: "",
    phone: "",
    email: "",
    customerType: "",
    commodity: "",
    value: "",
    frequency: "",
    details: "",
    sms: false,
    emailConsent: false,
  });

  /* HERO SLIDESHOW */
  useEffect(() => {
    const timer = setInterval(() => {
      setSlide((s) => (s + 1) % heroImages.length);
    }, 5000);
    return () => clearInterval(timer);
  }, []);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setForm({ ...form, [name]: type === "checkbox" ? checked : value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    await fetch("http://localhost:5000/haul-quote", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(form),
    });

    alert("✅ Rate quote submitted successfully!");
  };

  return (
    <div className="haulwithrgm-page">
      {/* ================= HERO ================= */}
      <section className="haulwithrgm-hero">
        <img src={heroImages[slide]} alt="Flatbed Freight" />
        <div className="haulwithrgm-hero-overlay">
          <div className="haulwithrgm-hero-content">
            <h1>Get Your Rate Quote</h1>
            <p>
              Truckload capacity available — fast, safe, and efficient flatbed
              freight solutions across North America.
            </p>
          </div>
        </div>
      </section>

      {/* ================= FORM ================= */}
      <div className="haulwithrgm-card">
        <form className="haulwithrgm-form" onSubmit={handleSubmit}>
          <input name="company" placeholder="Company Name *" required onChange={handleChange} />
          <input name="website" placeholder="Company Website" onChange={handleChange} />
          <input name="name" placeholder="Name *" required onChange={handleChange} />
          <input name="phone" placeholder="Phone Number *" required onChange={handleChange} />
          <input name="email" type="email" placeholder="Email *" required onChange={handleChange} />

          <select name="customerType" required onChange={handleChange}>
            <option value="">Customer Type *</option>
            <option>Broker</option>
            <option>Shipper</option>
            <option>Carrier</option>
          </select>

          <input name="commodity" placeholder="Commodity *" required onChange={handleChange} />
          <input name="value" placeholder="Dollar Value of Shipment *" required onChange={handleChange} />

          <select name="frequency" required onChange={handleChange}>
            <option value="">Shipment Frequency *</option>
            <option>1–5 loads/week</option>
            <option>6+ loads/week</option>
            <option>One-time or occasional</option>
          </select>

          <textarea
            name="details"
            placeholder="Tell us more about your freight needs"
            className="full"
            onChange={handleChange}
          />

          <label className="haulwithrgm-checkbox full">
            <input type="checkbox" name="sms" onChange={handleChange} />
            I agree to receive SMS communications
          </label>

          <label className="haulwithrgm-checkbox full">
            <input type="checkbox" name="emailConsent" onChange={handleChange} />
            I agree to receive email communications
          </label>

          <button className="haulwithrgm-submit full" type="submit">
            Submit Rate Quote
          </button>

          <p className="haulwithrgm-disclaimer full">
            By submitting this form, you consent to be contacted via email or
            phone. Message and data rates may apply.
          </p>
        </form>
      </div>
    </div>
  );
};

export default HaulWithRGM;
