import { useState, useEffect, useRef } from "react";
import Lottie from "lottie-react";
import "../styles/HaulWithRGM.css";
import characterAnimation from "../assets/animations/character.json";

function HaulWithRGM() {
  const [form, setForm] = useState({
    companyName: "",
    website: "",
    name: "",
    phone: "",
    email: "",
    frequency: "",
    details: "",
    smsConsent: false,
    emailConsent: false
  });

  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");

  const [botText, setBotText] = useState("");
  const [typed, setTyped] = useState("");
  const lottieRef = useRef(null);

  /* ü§ñ BOT TALK */
  const botSay = (text, from = 0, to = 30) => {
    setBotText(text);
    setTyped("");
    lottieRef.current?.playSegments([from, to], true);
  };

  /* ‚å®Ô∏è Typing animation */
  useEffect(() => {
    let i = 0;
    if (!botText) return;

    const speed = 22 + Math.random() * 10;

    const timer = setInterval(() => {
      setTyped((prev) => prev + botText[i]);
      i++;
      if (i >= botText.length) clearInterval(timer);
    }, speed);

    return () => clearInterval(timer);
  }, [botText]);

  /* üëã On load */
  useEffect(() => {
    botSay("Hi üëã Ready to get your rate quote?", 0, 40);
  }, []);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    const val = type === "checkbox" ? checked : value;

    setForm((p) => ({ ...p, [name]: val }));

    if (name === "companyName")
      botSay("Nice! Company saved üëç");
    else if (name === "phone")
      botSay("Perfect üìû We‚Äôll call here");
    else if (name === "email")
      botSay("Looks good üìß");
    else if (name === "frequency")
      botSay("Got it üöö");
    else if (name === "details")
      botSay("Thanks! Almost done ‚ú®");
    else if (name === "smsConsent" || name === "emailConsent")
      botSay("Consent received ‚úî", 80, 120);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!form.smsConsent && !form.emailConsent) {
      botSay("Please accept one consent ‚ö†Ô∏è", 60, 90);
      return;
    }

    try {
      setLoading(true);
      botSay("Sending your request üöÄ", 120, 160);

      const res = await fetch("http://localhost:5000/rate-quote", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form)
      });

      const data = await res.json();
      if (!res.ok) throw new Error();

      setMessage("‚úÖ Quote request sent!");
      botSay("Done! We‚Äôll contact you soon üéâ", 160, 210);

      setForm({
        companyName: "",
        website: "",
        name: "",
        phone: "",
        email: "",
        frequency: "",
        details: "",
        smsConsent: false,
        emailConsent: false
      });
    } catch {
      botSay("Oops! Try again ‚ùå", 60, 90);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="haul-container">
      <h1>Get Your Rate Quote</h1>
      <p>Fast, safe & reliable trucking with RGM</p>

      {/* ü§ñ BOT */}
      <div className="bot-area">
        <Lottie
          animationData={characterAnimation}
          lottieRef={lottieRef}
          loop
          className="bot-animation"
        />
        <div className="bot-bubble">{typed}</div>
      </div>

      {/* üìã FORM */}
      <form className="haul-form" onSubmit={handleSubmit}>
        <input name="companyName" placeholder="Company Name *" required value={form.companyName} onChange={handleChange} />
        <input name="website" placeholder="Company Website" value={form.website} onChange={handleChange} />
        <input name="name" placeholder="Your Name *" required value={form.name} onChange={handleChange} />
        <input name="phone" placeholder="Phone Number *" required value={form.phone} onChange={handleChange} />
        <input name="email" type="email" placeholder="Email *" required value={form.email} onChange={handleChange} />

        <select name="frequency" value={form.frequency} onChange={handleChange}>
          <option value="">Shipment Frequency</option>
          <option>1‚Äì5 loads/week</option>
          <option>6+ loads/week</option>
          <option>One-time shipment</option>
        </select>

        <textarea
          name="details"
          placeholder="Tell us about your freight"
          value={form.details}
          onChange={handleChange}
        />

        {/* ‚úÖ CONSENT */}
        <div className="consent-box">
          <label>
            <input type="checkbox" name="smsConsent" checked={form.smsConsent} onChange={handleChange} />
            <span>I agree to receive SMS from RGM</span>
          </label>

          <label>
            <input type="checkbox" name="emailConsent" checked={form.emailConsent} onChange={handleChange} />
            <span>I agree to receive emails from RGM</span>
          </label>
        </div>

        {message && <div className="form-message">{message}</div>}

        <button disabled={loading}>
          {loading ? "Submitting..." : "Submit"}
        </button>
      </form>
    </div>
  );
}

export default HaulWithRGM;
