import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "../styles/Haul.css";

/* HERO IMAGES */
import hero1 from "../assets/images/hero/hero1.jpg";
import hero2 from "../assets/images/hero/hero2.jpg";
import hero3 from "../assets/images/hero/hero3.jpg";
import hero4 from "../assets/images/hero/hero4.jpg";
import hero5 from "../assets/images/hero/hero5.jpg";
import hero6 from "../assets/images/hero/hero6.jpg";
import hero7 from "../assets/images/hero/hero7.jpg";
import hero8 from "../assets/images/hero/hero8.jpg";
import hero9 from "../assets/images/hero/hero9.jpg";
import hero10 from "../assets/images/hero/hero10.jpg";

const heroImages = [
  hero1, hero2, hero3, hero4, hero5,
  hero6, hero7, hero8, hero9, hero10
];

const Haul = () => {
  const [current, setCurrent] = useState(0);
  const navigate = useNavigate();

  /* HERO SLIDESHOW */
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrent((prev) => (prev + 1) % heroImages.length);
    }, 5000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="haul-page">
      {/* HERO SECTION */}
      <section
        className="haul-hero"
        style={{ backgroundImage: `url(${heroImages[current]})` }}
      >
        <div className="haul-overlay">
          <div className="haul-hero-content">
            <h1 className="haul-heading">
              Flatbed & Specialized Hauling
            </h1>

            <p className="haul-subheading">
              Trusted nationwide freight solutions for oversized, heavy, and time-critical shipments.
            </p>

            {/* HERO BUTTONS â€“ BOTTOM LEFT */}
            <div className="haul-hero-buttons">
              <button
                className="haul-btn primary"
                onClick={() => navigate("/haul-with-rgm")}
              >
                Free Rate Quote
              </button>

              <button
                className="haul-btn secondary"
                onClick={() => navigate("/contact")}
              >
                Contact Us
              </button>
            </div>
          </div>
        </div>
      </section>
    </div>
  );
};

export default Haul;
