import { useState, useRef } from "react";
import Lottie from "lottie-react";
import "../styles/HaulWithRGM.css";
import characterAnimation from "../assets/animations/character.json";

function HaulWithRGM() {
  const [form, setForm] = useState({
    companyName: "",
    companyWebsite: "",
    name: "",
    phone: "",
    email: "",
    customerType: "",
    commodity: "",
    shipmentValue: "",
    shipmentFrequency: "",
    freightDetails: "",
    agreeSms: false,
    agreeEmail: false
  });

  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");
  const [hint, setHint] = useState("üëã Hi! Let‚Äôs get your Melton Truck Lines rate quote.");

  const lottieRef = useRef();

  const play = (from, to) => {
    if (lottieRef.current) {
      lottieRef.current.playSegments([from, to], true);
    }
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    let val = type === "checkbox" ? checked : value;

    /* PHONE ‚Üí digits only */
    if (name === "phone") val = val.replace(/\D/g, "");

    setForm((prev) => ({ ...prev, [name]: val }));

    /* Character intelligence / hints */
    switch (name) {
      case "companyName":
        setHint(val.length < 2 ? "üè¢ Company name seems short" : "‚úÖ Company name saved");
        play(0, 30);
        break;
      case "email":
        const emailValid = /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(val);
        setHint(emailValid ? "üìß Email looks good" : "‚ùå Invalid email format");
        play(emailValid ? 61 : 121, emailValid ? 90 : 150);
        break;
      case "phone":
        setHint(val.length < 10 ? "üì± Enter a 10-digit phone number" : "üìû Phone number looks valid");
        play(val.length < 10 ? 121 : 91, val.length < 10 ? 150 : 120);
        break;
      case "agreeSms":
      case "agreeEmail":
        if (checked) {
          setHint("üëç Consent noted");
          play(151, 180);
        }
        break;
      default:
        setHint("‚úçÔ∏è Tell us more about your freight needs");
        play(0, 30);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!form.agreeSms && !form.agreeEmail) {
      setMessage("‚ùå Please agree to at least one communication method");
      play(121, 150);
      return;
    }

    try {
      setLoading(true);
      setMessage("");
      setHint("üöö Submitting your rate quote...");
      play(151, 180);

      const res = await fetch("http://localhost:5000/rate-quote", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form),
      });

      let data;
      try {
        data = await res.json();
      } catch {
        data = { message: res.statusText || "Submission failed" };
      }

      if (!res.ok) throw new Error(data.message || "Submission failed");

      setMessage("üéâ Rate quote submitted successfully!");
      setHint("ü•≥ Our Melton Truck Lines team will contact you soon!");
      play(181, 210);

      setForm({
        companyName: "",
        companyWebsite: "",
        name: "",
        phone: "",
        email: "",
        customerType: "",
        commodity: "",
        shipmentValue: "",
        shipmentFrequency: "",
        freightDetails: "",
        agreeSms: false,
        agreeEmail: false
      });
    } catch (err) {
      setMessage(`‚ùå ${err.message}`);
      setHint("‚ö†Ô∏è Something went wrong. Please try again.");
      play(121, 150);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="haul-container">
      <h1>Get Your Rate Quote</h1>
      <p>Truckload Capacity Available ‚Äì Get your freight on the road fast, safe, and efficiently with Melton Truck Lines.</p>

      <div className="character-wrapper">
        <Lottie
          animationData={characterAnimation}
          loop
          lottieRef={lottieRef}
          className="character-animation"
        />
        <div className="speech-bubble">{hint}</div>
      </div>

      <form className="haul-form" onSubmit={handleSubmit}>
        <div className="section-card full">
          <h3>Company Information</h3>
          <input name="companyName" placeholder="Company Name *" required value={form.companyName} onChange={handleChange} />
          <input name="companyWebsite" placeholder="Company Website *" required value={form.companyWebsite} onChange={handleChange} />
          <input name="name" placeholder="Name *" required value={form.name} onChange={handleChange} />
          <input name="phone" placeholder="Phone Number (xxx-xxx-xxxx) *" required value={form.phone} onChange={handleChange} />
          <input name="email" type="email" placeholder="Email *" required value={form.email} onChange={handleChange} />
        </div>

        <div className="section-card full">
          <h3>Shipment Details</h3>
          <select name="customerType" required value={form.customerType} onChange={handleChange}>
            <option value="">Customer Type *</option>
            <option value="Shipper">Shipper</option>
            <option value="Broker">Broker</option>
            <option value="Manufacturer">Manufacturer</option>
          </select>
          <input name="commodity" placeholder="Commodity *" required value={form.commodity} onChange={handleChange} />
          <input name="shipmentValue" placeholder="Dollar Value of Shipment *" required value={form.shipmentValue} onChange={handleChange} />
          <select name="shipmentFrequency" required value={form.shipmentFrequency} onChange={handleChange}>
            <option value="">Shipment Frequency *</option>
            <option value="1-5">1‚Äì5 loads/week</option>
            <option value="6+">6+ loads/week</option>
            <option value="one-time">One time or occasional shipment</option>
          </select>
          <textarea name="freightDetails" placeholder="Tell us more about your freight needs" value={form.freightDetails} onChange={handleChange} rows="4" />
        </div>

        <div className="section-card full">
          <label className="checkbox-container">
            <input type="checkbox" name="agreeSms" checked={form.agreeSms} onChange={handleChange} />
            By checking this box, you agree to receive SMS communications from Melton at the phone number provided. I agree
          </label>
          <label className="checkbox-container">
            <input type="checkbox" name="agreeEmail" checked={form.agreeEmail} onChange={handleChange} />
            By checking this box, you agree to receive email communications from us, including updates, promotions, and relevant industry insights. I agree
          </label>
        </div>

        <p className="form-note">
          By checking ‚ÄúI agree‚Äù and submitting this rate quote form, I agree that Melton Truck Lines is permitted to contact me via the email and/or phone number provided above. You may opt out of email, phone, and/or SMS communications at any time. Message and data rates may apply. For more information, please view our Privacy Policy.
        </p>

        {message && <div className="form-message">{message}</div>}

        <button className="submit-btn full" type="submit" disabled={loading}>
          {loading ? "Submitting..." : "Submit"}
        </button>
      </form>
    </div>
  );
}

export default HaulWithRGM;
