import React, { useEffect } from "react";
import { Link } from "react-router-dom";
import "../styles/Haul.css";

import hero1 from "../assets/images/hero/hero1.jpg";
import hero2 from "../assets/images/hero/hero2.jpg";
import hero3 from "../assets/images/hero/hero3.jpg";

const heroImages = [hero1, hero2, hero3];

const Haul = () => {
  useEffect(() => {
    let index = 0;
    const hero = document.querySelector(".haul-hero");

    hero.style.backgroundImage = `url(${heroImages[0]})`;

    const interval = setInterval(() => {
      index = (index + 1) % heroImages.length;
      hero.style.backgroundImage = `url(${heroImages[index]})`;
    }, 6500);

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="haul-page">
      {/* HERO */}
      <section className="haul-hero">
        <div className="haul-hero-overlay">
          <div className="haul-hero-inner">
            <h1>Flatbed & Specialized Hauling</h1>
            <p>
              Industry-leading flatbed freight solutions for oversized,
              heavy, and time-critical cargo across North America.
            </p>
          </div>

          <div className="haul-hero-actions">
            <Link to="/HaulWithRGM" className="btn-primary">
              Free Rate Quote
            </Link>
            <Link to="/Contact" className="btn-outline">
              Contact Us
            </Link>
          </div>
        </div>
      </section>

      {/* INTRO */}
      <section className="haul-section reveal">
        <h2>Experts in Flatbed Freight</h2>
        <p className="section-text">
          With decades of experience in flatbed transportation, RGM Logistics
          delivers dependable, long-haul freight solutions across the United
          States, Canada, and Mexico. Our late-model fleet, disciplined
          operations, and safety-first culture ensure your freight arrives
          securely and on time.
        </p>
      </section>

      {/* SERVICES */}
      <section className="haul-section light reveal">
        <h2>Flatbed Freight Services</h2>

        <div className="haul-services">
          <div className="service-card">USA Domestic Freight</div>
          <div className="service-card">Canada Freight Services</div>
          <div className="service-card">Mexico Freight Services</div>
          <div className="service-card">Over-Dimensional Freight</div>
        </div>
      </section>

      {/* WHY RGM */}
      <section className="haul-section reveal">
        <h2>Why Choose RGM Logistics</h2>

        <div className="haul-benefits">
          <p>100% company-owned trucks and professional drivers</p>
          <p>Late-model flatbed & step-deck equipment</p>
          <p>Flexible routing and trailer pool availability</p>
          <p>Safety-focused operations with consistent performance</p>
        </div>
      </section>

      {/* CTA */}
      <section className="haul-cta">
        <h2>Ship With Confidence</h2>
        <p>
          From standard flatbed loads to complex specialized freight, RGM
          Logistics delivers precision, reliability, and peace of mind.
        </p>
        <Link to="/HaulWithRGM" className="btn-primary large">
          Request a Free Quote
        </Link>
      </section>
    </div>
  );
};

export default Haul;
