import { useState, useRef } from "react";
import Lottie from "lottie-react";
import "../styles/HaulWithRGM.css";
import characterAnimation from "../assets/animations/character.json";

function RateQuote() {
  const [form, setForm] = useState({
    companyName: "",
    companyWebsite: "",
    name: "",
    phone: "",
    email: "",
    customerType: "",
    commodity: "",
    shipmentValue: "",
    shipmentFrequency: "",
    freightDetails: "",
    agreeSms: false,
    agreeEmail: false
  });

  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");
  const [hint, setHint] = useState("üëã Hi! I‚Äôll help you get a quick rate quote.");

  const lottieRef = useRef(null);

  const play = (from, to) => {
    lottieRef.current?.playSegments([from, to], true);
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    let val = type === "checkbox" ? checked : value;

    if (name === "phone") val = val.replace(/\D/g, "");

    setForm((prev) => ({ ...prev, [name]: val }));

    /* BOT INTELLIGENCE */
    switch (name) {
      case "companyName":
        setHint(val.length < 2 ? "üè¢ Please enter full company name" : "‚úÖ Company noted");
        break;

      case "phone":
        setHint(val.length < 10 ? "üì± Enter a valid 10-digit phone number" : "üìû Phone looks good");
        break;

      case "email":
        setHint(/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(val)
          ? "üìß Email verified"
          : "‚ùå Email format seems incorrect");
        break;

      case "shipmentFrequency":
        setHint("üöö Thanks! This helps us price accurately");
        break;

      case "agreeSms":
      case "agreeEmail":
        if (checked) setHint("üëç Communication preference saved");
        break;

      default:
        setHint("‚úçÔ∏è Keep going, you‚Äôre doing great!");
    }

    play(0, 30);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!form.agreeSms && !form.agreeEmail) {
      setMessage("‚ùå Please agree to at least one communication option.");
      play(121, 150);
      return;
    }

    try {
      setLoading(true);
      setHint("üöÄ Sending your request to RGM Logistics...");
      play(151, 180);

      await new Promise((r) => setTimeout(r, 1200)); // demo delay

      setMessage("üéâ Rate quote request submitted successfully!");
      setHint("ü•≥ Our team will reach out shortly!");
      play(181, 210);

      setForm({
        companyName: "",
        companyWebsite: "",
        name: "",
        phone: "",
        email: "",
        customerType: "",
        commodity: "",
        shipmentValue: "",
        shipmentFrequency: "",
        freightDetails: "",
        agreeSms: false,
        agreeEmail: false
      });
    } catch {
      setMessage("‚ùå Something went wrong. Please try again.");
      play(121, 150);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="apply-container">
      <h1>Get Your Rate Quote</h1>
      <p>Fast, safe, and efficient truckload solutions with RGM Logistics.</p>

      {/* BOT */}
      <div className="character-wrapper">
        <Lottie
          animationData={characterAnimation}
          lottieRef={lottieRef}
          loop
          className="character-animation"
        />
        <div className="speech-bubble">{hint}</div>
      </div>

      {/* FORM */}
      <form className="apply-form" onSubmit={handleSubmit}>
        <div className="section-card">
          <h3>Company Information</h3>
          <input name="companyName" placeholder="Company Name *" required value={form.companyName} onChange={handleChange} />
          <input name="companyWebsite" placeholder="Company Website *" required value={form.companyWebsite} onChange={handleChange} />
          <input name="name" placeholder="Your Name *" required value={form.name} onChange={handleChange} />
          <input name="phone" placeholder="Phone Number *" required value={form.phone} onChange={handleChange} />
          <input name="email" type="email" placeholder="Email *" required value={form.email} onChange={handleChange} />
        </div>

        <div className="section-card">
          <h3>Shipment Details</h3>
          <select name="customerType" required value={form.customerType} onChange={handleChange}>
            <option value="">Customer Type *</option>
            <option value="Shipper">Shipper</option>
            <option value="Broker">Broker</option>
            <option value="Manufacturer">Manufacturer</option>
          </select>

          <input name="commodity" placeholder="Commodity *" required value={form.commodity} onChange={handleChange} />
          <input name="shipmentValue" placeholder="Shipment Value *" required value={form.shipmentValue} onChange={handleChange} />

          <select name="shipmentFrequency" required value={form.shipmentFrequency} onChange={handleChange}>
            <option value="">Shipment Frequency *</option>
            <option value="1-5">1‚Äì5 loads/week</option>
            <option value="6+">6+ loads/week</option>
            <option value="one-time">One-time shipment</option>
          </select>

          <textarea
            name="freightDetails"
            rows="4"
            placeholder="Tell us more about your freight needs"
            value={form.freightDetails}
            onChange={handleChange}
          />
        </div>

        <div className="section-card">
          <label className="checkbox-row">
            <input type="checkbox" name="agreeSms" checked={form.agreeSms} onChange={handleChange} />
            <span>I agree to receive SMS communications</span>
          </label>

          <label className="checkbox-row">
            <input type="checkbox" name="agreeEmail" checked={form.agreeEmail} onChange={handleChange} />
            <span>I agree to receive email communications</span>
          </label>
        </div>

        {message && <div className="form-message">{message}</div>}

        <button className="submit-btn" disabled={loading}>
          {loading ? "Submitting..." : "Submit Rate Quote"}
        </button>
      </form>
    </div>
  );
}

export default RateQuote;
