import { useState, useRef } from "react";
import Lottie from "lottie-react";
import "../styles/ApplyToday.css";
import characterAnimation from "../assets/animations/character.json";

function ApplyToday() {
  const [form, setForm] = useState({
    firstName: "",
    middleName: "",
    lastName: "",
    suffix: "",
    ssn: "",
    dob: "",
    address1: "",
    address2: "",
    country: "United States",
    city: "",
    state: "",
    zip: "",
    residence3yrs: "",
    primaryPhone: "",
    cellPhone: "",
    email: "",
    confirmEmail: "",
    consent: false,
    license: ""
  });

  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");
  const [hint, setHint] = useState("üëã Hi! I‚Äôll help you fill this form.");

  const lottieRef = useRef();

  const play = (from, to) => {
    if (lottieRef.current) {
      lottieRef.current.playSegments([from, to], true);
    }
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    let val = type === "checkbox" ? checked : value;

    // NAME FIELDS ‚Üí LETTERS ONLY
    if (["firstName", "middleName", "lastName"].includes(name)) {
      val = val.replace(/[^a-zA-Z\s]/g, "");
    }

    // NUMBER FIELDS ‚Üí DIGITS ONLY
    if (["primaryPhone", "cellPhone", "ssn", "zip"].includes(name)) {
      val = val.replace(/\D/g, "");
    }

    setForm((prev) => ({ ...prev, [name]: val }));

    // CHARACTER INTELLIGENCE / HINTS
    if (name === "firstName") {
      setHint(val.length < 2 ? "‚ö†Ô∏è First name looks too short" : "üòä First name looks good");
      play(val.length < 2 ? 121 : 0, val.length < 2 ? 150 : 30);
    } else if (name === "lastName") {
      setHint("üëç Last name saved");
      play(31, 60);
    } else if (name === "email") {
      const emailValid = /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(val);
      setHint(emailValid ? "üìß Email looks correct" : "‚ùå Email format is invalid");
      play(emailValid ? 61 : 121, emailValid ? 90 : 150);
    } else if (name === "confirmEmail") {
      setHint(val === form.email ? "‚úÖ Emails match perfectly" : "‚ùå Emails don‚Äôt match");
      play(val === form.email ? 61 : 121, val === form.email ? 90 : 150);
    } else if (name === "primaryPhone") {
      setHint(val.length >= 10 ? "üìû Phone number looks valid" : "üì± Phone number must be at least 10 digits");
      play(val.length >= 10 ? 91 : 121, val.length >= 10 ? 120 : 150);
    } else if (name === "ssn") {
      setHint(val.length === 9 ? "üîê SSN looks valid" : "‚ö†Ô∏è SSN must be 9 digits");
      play(val.length === 9 ? 91 : 121, val.length === 9 ? 120 : 150);
    } else if (name === "consent" && checked) {
      setHint("üéâ Great! You can submit now");
      play(151, 180);
    } else {
      setHint("‚úçÔ∏è Keep going, great job!");
      play(0, 30);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (form.email !== form.confirmEmail) {
      setMessage("‚ùå Emails do not match");
      play(121, 150);
      return;
    }

    if (!form.consent) {
      setMessage("‚ùå You must agree to our terms to submit");
      play(121, 150);
      return;
    }

    try {
      setLoading(true);
      setMessage("");
      setHint("üöÄ Submitting your application...");
      play(151, 180);

      const res = await fetch("http://localhost:5000/apply", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form),
      });

      const data = await res.json();
      if (!res.ok) throw new Error(data.message || "Submission failed");

      setMessage("üéâ Application submitted successfully!");
      setHint("ü•≥ Your application has been sent!");
      play(181, 210);

      setForm({
        firstName: "",
        middleName: "",
        lastName: "",
        suffix: "",
        ssn: "",
        dob: "",
        address1: "",
        address2: "",
        country: "United States",
        city: "",
        state: "",
        zip: "",
        residence3yrs: "",
        primaryPhone: "",
        cellPhone: "",
        email: "",
        confirmEmail: "",
        consent: false,
        license: ""
      });
    } catch (err) {
      setMessage(`‚ùå ${err.message}`);
      setHint("‚ö†Ô∏è Could not send application. Try again.");
      play(121, 150);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="apply-container">
      <h1>Apply Today ‚Äì Join the RGM Family</h1>
      <p>Fill out the form below with your details to apply.</p>

      <div className="character-wrapper">
        <Lottie
          animationData={characterAnimation}
          loop
          lottieRef={lottieRef}
          className="character-animation"
        />
        <div className="speech-bubble">{hint}</div>
      </div>

      <form className="apply-form" onSubmit={handleSubmit}>

        {/* Personal Info */}
        <div className="section-card full">
          <h3>Personal Information</h3>
          <input name="firstName" placeholder="Your First Name *" required value={form.firstName} onChange={handleChange} />
          <input name="middleName" placeholder="Your Middle Name" value={form.middleName} onChange={handleChange} />
          <input name="lastName" placeholder="Your Last Name *" required value={form.lastName} onChange={handleChange} />
          <input name="suffix" placeholder="Suffix (Jr./Sr./III)" value={form.suffix} onChange={handleChange} />
          <input name="ssn" placeholder="Social Security Number *" required value={form.ssn} onChange={handleChange} />
          <input type="date" name="dob" required value={form.dob} onChange={handleChange} />
          <input name="license" placeholder="Driver's License Number *" required value={form.license} onChange={handleChange} />
        </div>

        {/* Address */}
        <div className="section-card full">
          <h3>Address</h3>
          <input name="address1" placeholder="Street Address *" required value={form.address1} onChange={handleChange} />
          <input name="address2" placeholder="Apartment / Suite" value={form.address2} onChange={handleChange} />
          <input name="city" placeholder="City *" required value={form.city} onChange={handleChange} />
          <input name="state" placeholder="State / Province *" required value={form.state} onChange={handleChange} />
          <input name="zip" placeholder="ZIP / Postal Code *" required value={form.zip} onChange={handleChange} />
        </div>

        {/* Contact */}
        <div className="section-card full">
          <h3>Contact Information</h3>
          <input name="primaryPhone" placeholder="Phone Number *" required value={form.primaryPhone} onChange={handleChange} />
          <input name="email" type="email" placeholder="Email Address *" required value={form.email} onChange={handleChange} />
          <input name="confirmEmail" type="email" placeholder="Confirm Email Address *" required value={form.confirmEmail} onChange={handleChange} />
          <label className="checkbox-container">
            <input type="checkbox" name="consent" checked={form.consent} onChange={handleChange} />
            I agree to the terms and to receive updates via email/SMS
          </label>
        </div>

        {message && <div className="form-message">{message}</div>}

        <button className="submit-btn full" type="submit" disabled={loading}>
          {loading ? "Submitting..." : "Submit Application"}
        </button>
      </form>
    </div>
  );
}

export default ApplyToday;
