import { useState, useEffect, useRef } from "react";
import Lottie from "lottie-react";
import "../styles/HaulWithRGM.css";
import botAnimation from "../assets/animations/character.json";

function HaulWithRGM() {
  const [form, setForm] = useState({
    companyName: "",
    website: "",
    name: "",
    phone: "",
    email: "",
    customerType: "",
    commodity: "",
    shipmentValue: "",
    shipmentFrequency: "",
    message: "",
    consentSMS: false,
    consentEmail: false
  });

  const [loading, setLoading] = useState(false);
  const [status, setStatus] = useState("");
  const [botText, setBotText] = useState("");
  const fullTextRef = useRef("");
  const lottieRef = useRef(null);

  /* ---------- BOT TYPING EFFECT ---------- */
  useEffect(() => {
    let i = 0;
    setBotText("");
    const interval = setInterval(() => {
      setBotText(fullTextRef.current.slice(0, i));
      i++;
      if (i > fullTextRef.current.length) clearInterval(interval);
    }, 22);
    return () => clearInterval(interval);
  }, [fullTextRef.current]);

  const say = (text, from = 0, to = 40) => {
    fullTextRef.current = text;
    if (lottieRef.current) {
      lottieRef.current.playSegments([from, to], true);
    }
  };

  useEffect(() => {
    say("üëã Hi! I‚Äôll help you get a fast rate quote.");
  }, []);

  /* ---------- FORM CHANGE ---------- */
  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    const val = type === "checkbox" ? checked : value;

    setForm((prev) => ({ ...prev, [name]: val }));

    if (name === "companyName") say("üè¢ Company name noted.");
    if (name === "email") say("üìß Make sure this email is correct.");
    if (name === "phone") say("üì± Phone looks good.");
    if (name === "commodity") say("üöö Got it! Freight details matter.");
  };

  /* ---------- SUBMIT ---------- */
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!form.consentSMS || !form.consentEmail) {
      say("‚ö†Ô∏è Please agree to both consent options.", 80, 120);
      return;
    }

    try {
      setLoading(true);
      say("üöÄ Sending your rate quote request...", 120, 180);

      const res = await fetch("http://localhost:5000/rate-quote", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form)
      });

      if (!res.ok) throw new Error("Submission failed");

      setStatus("‚úÖ Rate quote submitted successfully!");
      say("üéâ Done! Our team will contact you soon.", 180, 240);

      setForm({
        companyName: "",
        website: "",
        name: "",
        phone: "",
        email: "",
        customerType: "",
        commodity: "",
        shipmentValue: "",
        shipmentFrequency: "",
        message: "",
        consentSMS: false,
        consentEmail: false
      });
    } catch (err) {
      setStatus("‚ùå Failed to submit form");
      say("‚ùå Something went wrong. Please try again.", 80, 120);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="rgm-page">
      <h1>Get Your Rate Quote</h1>
      <p>Truckload capacity available ‚Äî fast, safe & reliable with RGM.</p>

      {/* -------- BOT (RIGHT CORNER) -------- */}
      <div className="bot-fixed">
        <Lottie
          lottieRef={lottieRef}
          animationData={botAnimation}
          loop
          className="bot-animation"
        />
        <div className="bot-bubble">{botText}</div>
      </div>

      {/* -------- FORM -------- */}
      <form className="rgm-form" onSubmit={handleSubmit}>
        <input name="companyName" placeholder="Company Name *" required value={form.companyName} onChange={handleChange} />
        <input name="website" placeholder="Company Website" value={form.website} onChange={handleChange} />
        <input name="name" placeholder="Your Name *" required value={form.name} onChange={handleChange} />
        <input name="phone" placeholder="Phone Number *" required value={form.phone} onChange={handleChange} />
        <input name="email" type="email" placeholder="Email *" required value={form.email} onChange={handleChange} />

        <select name="customerType" value={form.customerType} onChange={handleChange}>
          <option value="">Customer Type</option>
          <option>Shipper</option>
          <option>Broker</option>
        </select>

        <input name="commodity" placeholder="Commodity" value={form.commodity} onChange={handleChange} />
        <input name="shipmentValue" placeholder="Dollar Value of Shipment" value={form.shipmentValue} onChange={handleChange} />

        <select name="shipmentFrequency" value={form.shipmentFrequency} onChange={handleChange}>
          <option value="">Shipment Frequency</option>
          <option>1‚Äì5 loads/week</option>
          <option>6+ loads/week</option>
          <option>One time / occasional</option>
        </select>

        <textarea
          name="message"
          placeholder="Tell us more about your freight needs"
          value={form.message}
          onChange={handleChange}
        />

        {/* CONSENTS */}
        <label className="consent-row">
          <input type="checkbox" name="consentSMS" checked={form.consentSMS} onChange={handleChange} />
          <span>I agree to receive SMS communications from RGM.</span>
        </label>

        <label className="consent-row">
          <input type="checkbox" name="consentEmail" checked={form.consentEmail} onChange={handleChange} />
          <span>I agree to receive email communications from RGM.</span>
        </label>

        {status && <div className="status">{status}</div>}

        <button type="submit" disabled={loading}>
          {loading ? "Submitting..." : "Submit"}
        </button>
      </form>
    </div>
  );
}

export default HaulWithRGM;
