import React, { useEffect, useState, useRef } from "react";
import "../styles/HaulWithRGM.css";
import Lottie from "lottie-react";
import characterAnimation from "../assets/animations/character.json";

/* HERO SLIDES */
import hero1 from "../assets/images/hero/hero12.jpg";
import hero2 from "../assets/images/hero/hero14.jpg";
import hero3 from "../assets/images/hero/hero15.jpg";

const heroImages = [hero1, hero2, hero3];

const HaulWithRGM = () => {
  const [slide, setSlide] = useState(0);
  const [botOpen, setBotOpen] = useState(true);
  const [form, setForm] = useState({
    company: "",
    website: "",
    name: "",
    phone: "",
    email: "",
    customerType: "",
    commodity: "",
    dollarValue: "",
    frequency: "",
    details: "",
    smsConsent: false,
    emailConsent: false,
  });

  const [hint, setHint] = useState("üëã Hi! I‚Äôll help you fill this form.");
  const lottieRef = useRef();

  /* HERO SLIDESHOW */
  useEffect(() => {
    const timer = setInterval(
      () => setSlide((s) => (s + 1) % heroImages.length),
      5000
    );
    return () => clearInterval(timer);
  }, []);

  const play = (from, to) => {
    if (lottieRef.current) lottieRef.current.playSegments([from, to], true);
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    const val = type === "checkbox" ? checked : value;
    setForm({ ...form, [name]: val });

    /* Dynamic helper messages */
    switch (name) {
      case "company":
        setHint(val ? "üè¢ Company name noted" : "Please enter your company name");
        play(0, 30);
        break;
      case "website":
        setHint(val ? "üåê Website added" : "Enter your company website");
        play(0, 30);
        break;
      case "name":
        setHint(val.length >= 2 ? "üòä Name looks good" : "Enter your full name");
        play(0, 30);
        break;
      case "phone":
        setHint(
          val.length >= 10
            ? "üìû Phone number valid"
            : "Enter a valid 10-digit phone number"
        );
        play(31, 60);
        break;
      case "email":
        const emailValid = /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(val);
        setHint(emailValid ? "üìß Email looks correct" : "Enter a valid email address");
        play(emailValid ? 61 : 121, emailValid ? 90 : 150);
        break;
      case "customerType":
        setHint(val ? "Customer type selected" : "Select a customer type");
        play(0, 30);
        break;
      case "commodity":
        setHint(val ? "Commodity noted" : "Enter the commodity details");
        play(0, 30);
        break;
      case "dollarValue":
        setHint(val ? "Dollar value added" : "Enter estimated shipment value");
        play(0, 30);
        break;
      case "frequency":
        setHint(val ? "Frequency selected" : "Select shipment frequency");
        play(0, 30);
        break;
      case "details":
        setHint(val ? "Details captured" : "Provide additional shipment info");
        play(0, 30);
        break;
      default:
        setHint("Keep going, you're doing great!");
        play(0, 30);
    }
  };

  const handleSubmit = async (e) => {
  e.preventDefault();

  try {
    setHint("üöÄ Sending your rate quote...");
    play(151, 180);

    const res = await fetch("http://localhost:5000/rate-quote", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(form),
    });

    const data = await res.json();
    if (!res.ok) throw new Error("Failed to send email");

    setHint("üéâ Rate quote submitted successfully!");
    play(181, 210);

    setForm({
      company: "",
      website: "",
      name: "",
      phone: "",
      email: "",
      customerType: "",
      commodity: "",
      dollarValue: "",
      frequency: "",
      details: "",
      smsConsent: false,
      emailConsent: false,
    });
  } catch (err) {
    setHint("‚ùå Failed to send email. Please try again.");
    play(121, 150);
  }
};


  return (
    <div className="haulwithrgm-page">
      {/* HERO */}
      <section className="haulwithrgm-hero">
        <img src={heroImages[slide]} alt="Flatbed Freight" />
        <div className="haulwithrgm-hero-overlay">
          <div className="haulwithrgm-hero-content">
            <h1>Get Your Rate Quote</h1>
            <p>Truckload Capacity Available ‚Äì Get your freight on the road fast, safe, and efficiently with Melton Truck Lines.</p>
          </div>
        </div>
      </section>

      {/* FORM SECTION */}
      <section className="haulwithrgm-form-section">
        <div className="haulwithrgm-card">
          <h2>Request a Free Rate Quote</h2>
          <p className="subtitle">Fill out the form below and our team will get back to you promptly.</p>

          <div className="character-wrapper">
            <Lottie
              animationData={characterAnimation}
              lottieRef={lottieRef}
              loop
              className="character-animation"
            />
            <div className="speech-bubble">{hint}</div>
          </div>

          <form className="haulwithrgm-form" onSubmit={handleSubmit}>
            <input name="company" placeholder="Company Name *" required value={form.company} onChange={handleChange} />
            <input name="website" placeholder="Company Website *" required value={form.website} onChange={handleChange} />
            <input name="name" placeholder="Name *" required value={form.name} onChange={handleChange} />
            <input name="phone" placeholder="Phone Number (xxx-xxx-xxxx) *" required value={form.phone} onChange={handleChange} />
            <input name="email" placeholder="Email *" type="email" required value={form.email} onChange={handleChange} />
            <input name="customerType" placeholder="Customer Type *" required value={form.customerType} onChange={handleChange} />
            <input name="commodity" placeholder="Commodity *" required value={form.commodity} onChange={handleChange} />
            <input name="dollarValue" placeholder="Dollar Value of Shipment *" required value={form.dollarValue} onChange={handleChange} />

            <select name="frequency" required value={form.frequency} onChange={handleChange}>
              <option value="">Shipment Frequency *</option>
              <option>1-5 loads/week</option>
              <option>6+ loads/week</option>
              <option>One time / occasional shipment</option>
            </select>

            <textarea
              name="details"
              placeholder="Tell us more about your freight needs"
              value={form.details}
              onChange={handleChange}
              className="full"
            />

            <label className="checkbox-container">
              <input type="checkbox" name="smsConsent" checked={form.smsConsent} onChange={handleChange} />
              By checking this box, you agree to receive SMS communications from Melton at the phone number provided.
            </label>

            <label className="checkbox-container">
              <input type="checkbox" name="emailConsent" checked={form.emailConsent} onChange={handleChange} />
              By checking this box, you agree to receive email communications from us, including updates, promotions, and relevant industry insights.
            </label>

            <button className="haulwithrgm-submit full" type="submit">Submit</button>
          </form>
        </div>
      </section>
    </div>
  );
};

export default HaulWithRGM;
