import { useState, useRef } from "react";
import Lottie from "lottie-react";
import "../styles/ApplyToday.css";
import characterAnimation from "../assets/animations/character.json";
import ApplyToday from "./joinRGM/ApplyToday";
import Haul from "./Haul";

function HaulWithRGM() {
  const [form, setForm] = useState({
    companyName: "",
    companyWebsite: "",
    contactName: "",
    phone: "",
    email: "",
    customerType: "",
    commodity: "",
    shipmentValue: "",
    shipmentFrequency: "",
    freightDetails: "",
    smsConsent: false,
    emailConsent: false,
  });

  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");
  const [hint, setHint] = useState("üëã Hi! I‚Äôll help you fill this form.");

  const lottieRef = useRef();

  const play = (from, to) => {
    if (lottieRef.current) {
      lottieRef.current.playSegments([from, to], true);
    }
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    let val = type === "checkbox" ? checked : value;

    // For phone, allow only digits
    if (name === "phone") {
      val = val.replace(/\D/g, "");
    }

    setForm((prev) => ({ ...prev, [name]: val }));

    // Simple hint messages based on field
    if (name === "contactName") {
      setHint(val.length < 2 ? "‚ö†Ô∏è Name looks too short" : "üòä Name looks good");
      play(val.length < 2 ? 121 : 0, val.length < 2 ? 150 : 30);
    } else if (name === "email") {
      const emailValid = /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(val);
      setHint(emailValid ? "üìß Email looks correct" : "‚ùå Email format is invalid");
      play(emailValid ? 61 : 121, emailValid ? 90 : 150);
    } else if (name === "phone") {
      setHint(val.length >= 10 ? "üìû Phone number looks valid" : "üì± Phone number must be at least 10 digits");
      play(val.length >= 10 ? 91 : 121, val.length >= 10 ? 120 : 150);
    } else {
      setHint("‚úçÔ∏è Keep going, great job!");
      play(0, 30);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!form.smsConsent && !form.emailConsent) {
      setMessage("‚ùå You must agree to at least one consent checkbox to submit");
      play(121, 150);
      return;
    }

    try {
      setLoading(true);
      setMessage("");
      setHint("üöÄ Submitting your rate quote...");
      play(151, 180);

      const res = await fetch("http://localhost:5000/rate-quote", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form),
      });

      const data = await res.json();
      if (!res.ok) throw new Error(data.message || "Submission failed");

      setMessage("üéâ Rate quote submitted successfully!");
      setHint("ü•≥ Your information has been sent!");
      play(181, 210);

      // Reset form
      setForm({
        companyName: "",
        companyWebsite: "",
        contactName: "",
        phone: "",
        email: "",
        customerType: "",
        commodity: "",
        shipmentValue: "",
        shipmentFrequency: "",
        freightDetails: "",
        smsConsent: false,
        emailConsent: false,
      });
    } catch (err) {
      setMessage(`‚ùå ${err.message}`);
      setHint("‚ö†Ô∏è Could not send rate quote. Try again.");
      play(121, 150);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="apply-container">
      <h1>Get Your Rate Quote</h1>
      <p>Truckload Capacity Available ‚Äì Get your freight on the road fast, safe, and efficiently with Melton Truck Lines.</p>

      <div className="character-wrapper">
        <Lottie
          animationData={characterAnimation}
          loop
          lottieRef={lottieRef}
          className="character-animation"
        />
        <div className="speech-bubble">{hint}</div>
      </div>

      <form className="apply-form" onSubmit={handleSubmit}>

        {/* Company Info */}
        <div className="section-card full">
          <h3>Company Information</h3>
          <input name="companyName" placeholder="Company Name *" required value={form.companyName} onChange={handleChange} />
          <input name="companyWebsite" placeholder="Company Website *" required value={form.companyWebsite} onChange={handleChange} />
        </div>

        {/* Contact Info */}
        <div className="section-card full">
          <h3>Contact Information</h3>
          <input name="contactName" placeholder="Name *" required value={form.contactName} onChange={handleChange} />
          <input name="phone" placeholder="Phone Number (xxx-xxx-xxxx) *" required value={form.phone} onChange={handleChange} />
          <input name="email" type="email" placeholder="Email *" required value={form.email} onChange={handleChange} />
        </div>

        {/* Shipment Info */}
        <div className="section-card full">
          <h3>Shipment Information</h3>
          <input name="customerType" placeholder="Customer Type *" required value={form.customerType} onChange={handleChange} />
          <input name="commodity" placeholder="Commodity *" required value={form.commodity} onChange={handleChange} />
          <input name="shipmentValue" placeholder="Dollar Value of Shipment *" required value={form.shipmentValue} onChange={handleChange} />
          <label>Shipment Frequency</label>
          <select name="shipmentFrequency" value={form.shipmentFrequency} onChange={handleChange} required>
            <option value="">Select Frequency</option>
            <option value="1-5 loads/week">1-5 loads/week</option>
            <option value="6+ loads/week">6+ loads/week</option>
            <option value="one-time">One time or occasional shipment (not recurring)</option>
          </select>
          <textarea name="freightDetails" placeholder="Tell us more about your freight needs" value={form.freightDetails} onChange={handleChange}></textarea>
        </div>

        {/* Consents */}
        <div className="section-card full">
          <label className="checkbox-container">
            <input type="checkbox" name="smsConsent" checked={form.smsConsent} onChange={handleChange} />
            By checking this box, you agree to receive SMS communications from Melton at the phone number provided.
          </label>
          <label className="checkbox-container">
            <input type="checkbox" name="emailConsent" checked={form.emailConsent} onChange={handleChange} />
            By checking this box, you agree to receive email communications from us, including updates, promotions, and relevant industry insights.
          </label>
          <p className="privacy-note">
            By checking ‚ÄúI agree‚Äù and submitting this rate quote form, you agree that Melton Truck Lines may contact you via the email and/or phone number provided. You may opt out at any time. Message and data rates may apply. See our Privacy Policy for more info.
          </p>
        </div>

        {message && <div className="form-message">{message}</div>}

        <button className="submit-btn full" type="submit" disabled={loading}>
          {loading ? "Submitting..." : "Submit"}
        </button>
      </form>
    </div>
  );
}

export default HaulWithRGM;
