import { useState } from "react";
import "../../styles/HaulWithRGM.css";

function HaulWithRGM() {
  const [form, setForm] = useState({
    companyName: "",
    companyWebsite: "",
    name: "",
    phone: "",
    email: "",
    customerType: "",
    commodity: "",
    shipmentValue: "",
    frequency: "",
    details: "",
    smsConsent: false,
    emailConsent: false,
  });

  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setForm((prev) => ({
      ...prev,
      [name]: type === "checkbox" ? checked : value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!form.email || !form.phone) {
      setMessage("❌ Email and Phone are required");
      return;
    }

    try {
      setLoading(true);
      setMessage("");

      const res = await fetch("http://localhost:5000/rate-quote", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form),
      });

      const data = await res.json();
      if (!res.ok) throw new Error(data.message || "Submission failed");

      setMessage("✅ Your rate quote request has been sent successfully!");

      setForm({
        companyName: "",
        companyWebsite: "",
        name: "",
        phone: "",
        email: "",
        customerType: "",
        commodity: "",
        shipmentValue: "",
        frequency: "",
        details: "",
        smsConsent: false,
        emailConsent: false,
      });
    } catch (err) {
      setMessage(`❌ ${err.message}`);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="haul-quote-container">
      <h1>Get Your Rate Quote</h1>
      <p className="subtitle">
        Truckload Capacity Available – Get your freight on the road fast, safe,
        and efficiently with RGM Logistics.
      </p>

      <form className="haul-quote-form" onSubmit={handleSubmit}>
        <input
          name="companyName"
          placeholder="Company Name *"
          required
          value={form.companyName}
          onChange={handleChange}
        />

        <input
          name="companyWebsite"
          placeholder="Company Website"
          value={form.companyWebsite}
          onChange={handleChange}
        />

        <input
          name="name"
          placeholder="Your Name *"
          required
          value={form.name}
          onChange={handleChange}
        />

        <input
          name="phone"
          placeholder="Phone Number (xxx-xxx-xxxx) *"
          required
          value={form.phone}
          onChange={handleChange}
        />

        <input
          type="email"
          name="email"
          placeholder="Email *"
          required
          value={form.email}
          onChange={handleChange}
        />

        <select name="customerType" value={form.customerType} onChange={handleChange}>
          <option value="">Customer Type *</option>
          <option>Shipper</option>
          <option>Broker</option>
          <option>Manufacturer</option>
        </select>

        <input
          name="commodity"
          placeholder="Commodity *"
          value={form.commodity}
          onChange={handleChange}
        />

        <input
          name="shipmentValue"
          placeholder="Dollar Value of Shipment *"
          value={form.shipmentValue}
          onChange={handleChange}
        />

        <select name="frequency" value={form.frequency} onChange={handleChange}>
          <option value="">Shipment Frequency</option>
          <option>1–5 loads/week</option>
          <option>6+ loads/week</option>
          <option>One-time or occasional shipment</option>
        </select>

        <textarea
          name="details"
          placeholder="Tell us more about your freight needs"
          value={form.details}
          onChange={handleChange}
        />

        <label className="checkbox">
          <input
            type="checkbox"
            name="smsConsent"
            checked={form.smsConsent}
            onChange={handleChange}
          />
          I agree to receive SMS communications
        </label>

        <label className="checkbox">
          <input
            type="checkbox"
            name="emailConsent"
            checked={form.emailConsent}
            onChange={handleChange}
          />
          I agree to receive email communications
        </label>

        {message && <div className="form-message">{message}</div>}

        <button type="submit" disabled={loading}>
          {loading ? "Submitting..." : "Submit"}
        </button>

        <p className="disclaimer">
          By submitting this form, you agree that RGM Logistics may contact you
          via email, phone, or SMS. You may opt out at any time.
        </p>
      </form>
    </div>
  );
}

export default HaulWithRGM;
